    26→<script setup>
    27→import { ref, computed, watch, onMounted, onUnmounted } from 'vue';
    28→import { storeToRefs } from 'pinia';
    29→import { useOrganizerStore } from '../stores/organizer.js';
    30→import { useAuthStore } from '../../../core/stores/auth.js';
    31→import tagSubcollectionService from '../services/tagSubcollectionService.js';
    32→import TagDisplaySection from './TagDisplaySection.vue';
    33→import CategoryTagSelector from './CategoryTagSelector.vue';
    34→
    35→const props = defineProps({
    36→  evidence: {
    37→    type: Object,
    38→    required: true
    39→  },
    40→  disabled: {
    41→    type: Boolean,
    42→    default: false
    43→  },
    44→  loading: {
    45→    type: Boolean,
    46→    default: false
    47→  }
    48→});
    49→
    50→const emit = defineEmits([
    51→  'tags-updated', 
    52→  'migrate-legacy', 
    53→  'approve-ai-tag', 
    54→  'reject-ai-tag', 
    55→  'bulk-approve-ai-tags', 
    56→  'bulk-reject-ai-tags'
    57→]);
    58→
    59→// Stores
    60→const organizerStore = useOrganizerStore();
    61→const authStore = useAuthStore();
    62→const { categories } = storeToRefs(organizerStore);
    63→
    64→// Services
    65→const tagService = tagSubcollectionService;
    66→
    67→// Local state
    68→const hideMigrationPrompt = ref(false);
    69→const pendingTags = ref([]);
    70→const approvedTags = ref([]);
    71→const rejectedTags = ref([]);
    72→const loadingTags = ref(false);
    73→const unsubscribeTags = ref(null);
    74→const categoryTagSelector = ref(null);
    75→
    76→// Computed properties
    77→const structuredTags = computed(() => {
    78→  // Combine approved tags (including auto-approved AI tags) with manually added tags
    79→  const allApprovedTags = approvedTags.value;
    80→  
    81→  // If no subcollection tags, fallback to embedded arrays for backward compatibility
    82→  if (allApprovedTags.length === 0 && pendingTags.value.length === 0) {
    83→    return [
    84→      // Legacy embedded tags no longer supported - using subcollection only
    85→    ];
    86→  }
    87→  
    88→  return allApprovedTags;
    89→});
    90→
    91→// Computed property for AI pending tags (awaiting review)
    92→const aiPendingTags = computed(() => pendingTags.value);
    93→
    94→// Computed property for all AI tags (pending + approved + rejected)
    95→const allAITags = computed(() => {
    96→  return [...pendingTags.value, ...approvedTags.value.filter(tag => tag.source === 'ai'), ...rejectedTags.value];
    97→});
    98→
    99→// Legacy tags no longer supported - using subcollection tags only
   100→
