   102→const onCategoryChange = (categoryId) => {
   103→  // Category change is now handled by child component
   104→  console.log('Category changed:', categoryId);
   105→};
   106→
   107→const onTagChange = (tagId) => {
   108→  // Tag change is now handled by child component
   109→  console.log('Tag changed:', tagId);
   110→};
