   112→const addSelectedTag = async (selection) => {
   113→  if (!selection || !selection.category || !selection.tag) return;
   114→  
   115→  const { category: selectedCategory, tag: selectedTag } = selection;
   116→  
   117→  // Check if tag already exists (exact same tag)
   118→  const tagExists = structuredTags.value.some(tag => 
   119→    tag.metadata?.categoryId === selectedCategory.id && tag.tagName === selectedTag.name
   120→  );
   121→  
   122→  if (tagExists) {
   123→    console.warn('Tag already exists:', selectedTag.name);
   124→    return;
   125→  }
   126→  
   127→  try {
   128→    // Remove existing tags from same category (mutual exclusivity) first
   129→    const existingTagsInCategory = structuredTags.value.filter(tag => 
   130→      tag.metadata?.categoryId === selectedCategory.id
   131→    );
   132→    
   133→    for (const existingTag of existingTagsInCategory) {
   134→      if (existingTag.id) {
   135→        const teamId = authStore.currentTeam;
   136→      if (teamId) {
   137→        await tagService.deleteTag(props.evidence.id, existingTag.id, teamId);
   138→      }
   139→      }
   140→    }
   141→    
   142→    // Create new structured tag data (using NEW data structure from migration plan)
   143→    const newTagData = {
   144→      categoryId: selectedCategory.id,
   145→      categoryName: selectedCategory.name,
   146→      tagName: selectedTag.name,
   147→      color: selectedTag.color,
   148→      source: 'human',
   149→      confidence: 1.0, // Human tags have 100% confidence (1.0 scale)
   150→      autoApproved: null, // Not applicable for human tags
   151→      reviewRequired: false, // Human tags don't need review
   152→      createdBy: authStore.user?.uid || 'unknown-user',
   153→      metadata: {
   154→        userNote: 'Manual tag selection',
   155→        manuallyApplied: true,
   156→        selectedFromOptions: [selectedTag.name] // Could be expanded to show other options
   157→      }
   158→    };
   159→    
   160→    // Add tag using subcollection service (auto-approved since it's manual)
   161→    const teamId = authStore.currentTeam;
   162→    if (!teamId) {
   163→      console.error('No team ID available for tag operations');
   164→      return;
   165→    }
   166→    await tagService.addTag(props.evidence.id, newTagData, teamId);
   167→    
   168→    // Reload tags to update UI immediately
   169→    await loadSubcollectionTags();
   170→    
   171→    emit('tags-updated');
   172→    
   173→    // Reset selections in child component
   174→    if (categoryTagSelector.value) {
   175→      categoryTagSelector.value.clearSelections();
   176→    }
   177→    
   178→  } catch (error) {
   179→    console.error('Failed to add structured tag:', error);
   180→  }
   181→};
   182→
   183→const removeStructuredTag = async (tagToRemove) => {
   184→  try {
   185→    // Use subcollection service to remove tag
   186→    if (tagToRemove.id) {
   187→      // Tag has subcollection ID - use subcollection service
   188→      const teamId = authStore.currentTeam;
   189→      if (!teamId) {
   190→        console.error('No team ID available for tag operations');
   191→        return;
   192→      }
   193→      await tagService.deleteTag(props.evidence.id, tagToRemove.id, teamId);
   194→    } else {
   195→      // Fallback to embedded array method for backward compatibility
   196→      const freshEvidence = organizerStore.stores.core.getEvidenceById(props.evidence.id);
   197→      if (!freshEvidence) {
   198→        throw new Error('Evidence not found in store');
   199→      }
   200→      
   201→      // Legacy embedded tag updates no longer supported - using subcollection only
   202→      console.warn('Legacy embedded tag removal attempted - use subcollection service instead');
   203→    }
   204→    
   205→    // Small delay to ensure Firestore operation is committed before reloading
   206→    await new Promise(resolve => setTimeout(resolve, 100));
   207→    
   208→    // Reload tags to update UI immediately
   209→    await loadSubcollectionTags();
   210→    
   211→    emit('tags-updated');
   212→    
   213→  } catch (error) {
   214→    console.error('Failed to remove structured tag:', error);
   215→  }
   216→};
   217→
   218→// New methods for handling AI tag approvals
   219→const approveAITag = async (tagId) => {
   220→  try {
   221→    const teamId = authStore.currentTeam;
   222→    if (!teamId) {
   223→      console.error('No team ID available for tag operations');
   224→      return;
   225→    }
   226→    await tagService.approveTag(props.evidence.id, tagId, teamId);
   227→    await loadSubcollectionTags(); // Reload to update state
   228→    emit('tags-updated');
   229→    emit('approve-ai-tag', tagId);
   230→  } catch (error) {
   231→    console.error('Failed to approve AI tag:', error);
   232→  }
   233→};
   234→
   235→const rejectAITag = async (tagId) => {
   236→  try {
   237→    const teamId = authStore.currentTeam;
   238→    if (!teamId) {
   239→      console.error('No team ID available for tag operations');
   240→      return;
   241→    }
   242→    await tagService.rejectTag(props.evidence.id, tagId, teamId);
   243→    await loadSubcollectionTags(); // Reload to update state
   244→    emit('tags-updated');
   245→    emit('reject-ai-tag', tagId);
   246→  } catch (error) {
   247→    console.error('Failed to reject AI tag:', error);
   248→  }
   249→};
   250→
   251→const bulkApproveAITags = async (tagIds) => {
   252→  try {
   253→    const teamId = authStore.currentTeam;
   254→    if (!teamId) {
   255→      console.error('No team ID available for tag operations');
   256→      return;
   257→    }
   258→    await tagService.approveTagsBatch(props.evidence.id, tagIds, teamId);
   259→    await loadSubcollectionTags(); // Reload to update state
   260→    emit('tags-updated');
   261→    emit('bulk-approve-ai-tags', tagIds);
   262→  } catch (error) {
   263→    console.error('Failed to bulk approve AI tags:', error);
   264→  }
   265→};
   266→
   267→const bulkRejectAITags = async (tagIds) => {
   268→  try {
   269→    const teamId = authStore.currentTeam;
   270→    if (!teamId) {
   271→      console.error('No team ID available for tag operations');
   272→      return;
   273→    }
   274→    await tagService.rejectTagsBatch(props.evidence.id, tagIds, teamId);
   275→    await loadSubcollectionTags(); // Reload to update state
   276→    emit('tags-updated');
   277→    emit('bulk-reject-ai-tags', tagIds);
   278→  } catch (error) {
   279→    console.error('Failed to bulk reject AI tags:', error);
   280→  }
   281→};
   282→
   283→// Legacy tag removal no longer supported
   284→// Lifecycle hooks
   285→onMounted(async () => {
   286→  await loadSubcollectionTags();
   287→});
   288→
   289→onUnmounted(() => {
   290→  if (unsubscribeTags.value) {
   291→    unsubscribeTags.value();
   292→    unsubscribeTags.value = null;
   293→  }
   294→});
   295→
   296→// Watch for evidence changes to reload tags
   297→watch(() => props.evidence.id, async () => {
   298→  if (unsubscribeTags.value) {
   299→    unsubscribeTags.value();
   300→    unsubscribeTags.value = null;
   301→  }
   302→  await loadSubcollectionTags();
   303→});
   304→
   305→/**
   306→ * Load tags from subcollection grouped by status
   307→ */
   308→const loadSubcollectionTags = async () => {
   309→  if (!props.evidence.id) return;
   310→  
   311→  try {
   312→    loadingTags.value = true;
   313→    
   314→    // Load tags grouped by status
   315→    const teamId = authStore.currentTeam;
   316→    if (!teamId) {
   317→      console.error('[TagSelector] No team ID available');
   318→      return;
   319→    }
   320→    
   321→    const tagsByStatus = await tagService.getTagsByStatus(props.evidence.id, teamId);
   322→    
   323→    pendingTags.value = tagsByStatus.pending || [];
   324→    approvedTags.value = tagsByStatus.approved || [];
   325→    rejectedTags.value = tagsByStatus.rejected || [];
   326→    
   327→    loadingTags.value = false;
   328→    
   329→  } catch (error) {
   330→    console.error('Failed to load subcollection tags:', error);
   331→    loadingTags.value = false;
   332→    // Don't clear existing tags on error - keep the current state
   333→    // This prevents the UI from temporarily showing no tags
   334→  }
   335→};
   336→
   337→// Expose methods for parent component access
   338→defineExpose({
   339→  approveAITag,
   340→  rejectAITag,
   341→  bulkApproveAITags,
   342→  bulkRejectAITags,
   343→  aiPendingTags,
   344→  allAITags,
   345→  loadSubcollectionTags
   346→});
   347→
   348→</script>
   349→
   350→<style scoped>
   351→.tag-selector {
   352→  max-width: 100%;
   353→}
   354→</style>

