   105→   */
   106→  async getEvidenceDocument(evidenceId, teamId) {
   107→    return this.evidenceService.getEvidenceDocument(evidenceId, teamId);
   108→  }
   109→
   110→  /**
   111→   * Retrieve file content from Firebase Storage for AI processing
   112→   * @param {Object} evidence - Evidence document
   113→   * @returns {Promise<string>} - Base64 encoded file content for AI processing
   114→   */
   115→  async getFileForProcessing(evidence) {
   116→    const teamId = this.getTeamId();
   117→    return this.fileProcessingService.getFileForProcessing(evidence, teamId);
   118→  }
   119→
   120→  /**
   121→   * Get user's categories to provide context for AI suggestions
   122→   * @param {string} teamId - Team ID
   123→   * @returns {Promise<Array>} - Array of category objects
   124→   */
   125→  async getUserCategories(teamId) {
   126→    return this.evidenceService.getUserCategories(teamId);
   127→  }
   128→
   129→  /**
   130→   * Generate tag suggestions using Firebase Vertex AI
   131→   * @param {string} base64Data - Base64 encoded file content
   132→   * @param {Array} categories - User's categories
   133→   * @param {Object} evidence - Evidence document
   134→   * @param {string} extension - File extension as fallback for MIME type
   135→   * @returns {Promise<Array>} - Array of suggested tags
   136→   */
   137→  async generateTagSuggestions(base64Data, categories, evidence, extension = 'pdf') {
   138→    return this.aiProcessingService.generateTagSuggestions(base64Data, categories, evidence, extension);
   139→  }
   140→
   141→  /**
   142→   * Parse AI response and validate suggestions
   143→   * @param {string} aiResponse - AI response text
   144→   * @param {Array} categories - Available categories
   145→   * @returns {Array} - Validated suggested tags
   146→   */
   147→  parseAIResponse(aiResponse, categories) {
   148→    return this.aiProcessingService.parseAIResponse(aiResponse, categories);
   149→  }
   150→
   151→  /**
   152→   * Store AI suggestions in subcollection with confidence-based approval
   153→   * @param {string} evidenceId - Evidence document ID
   154→   * @param {Array} suggestions - AI tag suggestions with confidence scores
   155→   * @returns {Promise<Array>} - Array of stored tag documents
   156→   */
   157→  async storeAISuggestionsWithConfidence(evidenceId, suggestions) {
   158→    try {
   159→      const teamId = this.getTeamId();
   160→      
   161→      // Transform suggestions to include confidence and metadata
   162→      const tagData = suggestions.map(suggestion => {
   163→        // Convert decimal confidence (0.9) to percentage (90)
   164→        let confidence = suggestion.confidence || 0;
   165→        if (confidence <= 1) {
   166→          confidence = Math.round(confidence * 100);
   167→        } else {
   168→          confidence = Math.round(confidence);
   169→        }
   170→        
   171→        return {
   172→          tagName: suggestion.tagName || suggestion.name,
   173→          confidence: confidence,
   174→          source: 'ai',
   175→          metadata: {
   176→            model: 'vertex-ai',
   177→            processingTime: suggestion.processingTime || 0,
   178→            context: suggestion.reasoning || ''
   179→          }
   180→        };
   181→      });
   182→
   183→      // Store tags in subcollection (auto-approval handled by service)
   184→      const storedTags = await tagSubcollectionService.addTagsBatch(evidenceId, tagData, teamId);
   185→      
   186→      return storedTags;
   187→    } catch (error) {
   188→      console.error('[AITagService] Error storing AI suggestions:', error);
   189→      throw error;
   190→    }
   191→  }
