   112→  // Get manually added tags (source: 'manual') and approved AI tags that should show as approved
   113→  const manualTags = approvedTags.value.filter(tag => tag.source === 'manual');
   114→  
   115→  // Legacy embedded arrays no longer supported
   116→  
   117→  return manualTags;
   118→});
   119→
   120→const structuredAITags = computed(() => {
   121→  // Get all AI tags (pending, approved, rejected) with proper status mapping
   122→  const allAITags = [
   123→    ...pendingTags.value.map(tag => ({ ...tag, displayStatus: 'pending' })),
   124→    ...approvedTags.value.filter(tag => tag.source === 'ai').map(tag => ({ ...tag, displayStatus: 'approved' })),
   125→    ...rejectedTags.value.map(tag => ({ ...tag, displayStatus: 'rejected' }))
   126→  ];
   127→  
   128→  // Legacy embedded arrays no longer supported
   129→  
   130→  // Map to format expected by AITagChip component
   131→  return allAITags.map(tag => ({
   132→    ...tag,
   133→    status: tag.displayStatus || tag.status,
   134→    confidence: tag.confidence || 80,
   135→    reasoning: tag.metadata?.context || tag.reasoning || 'AI suggested',
   136→    suggestedAt: tag.createdAt,
   137→    categoryName: tag.metadata?.categoryName || 'Unknown Category'
   138→  }));
   139→});
   140→
   141→// Legacy tags no longer supported
