   148→});
   149→
   150→const lowConfidenceAITags = computed(() => {
   151→  return structuredAITags.value.filter(tag => tag.confidence < 70);
   152→});
   153→
   154→const hasAnyTags = computed(() => {
   155→  return structuredHumanTags.value.length > 0 || 
   156→         structuredAITags.value.length > 0 || 
   157→         false; // Legacy tags no longer supported
   158→});
   159→
   160→const hasHighConfidencePendingTags = computed(() => {
   161→  return pendingTags.value.some(tag => tag.confidence >= 85);
   162→});
   163→
   164→// Event handlers
   165→const handleTagsUpdated = () => {
   166→  // TagSelector handles the update internally, just emit for refresh
   167→  emit('tags-updated');
   168→  // Reload subcollection tags after update
   169→  loadSubcollectionTags();
   170→};
