   179→  emit('tag-error', error);
   180→};
   181→
   182→// Lifecycle hooks
   183→onMounted(async () => {
   184→  await loadSubcollectionTags();
   185→});
   186→
   187→onUnmounted(() => {
   188→  // Cleanup handled by TagSelector component
   189→});
   190→
   191→// Watch for evidence changes to reload tags
   192→watch(() => props.evidence.id, async () => {
   193→  await loadSubcollectionTags();
   194→});
   195→
   196→/**
   197→ * Load tags from subcollection grouped by status for confidence-based display
   198→ */
   199→const loadSubcollectionTags = async () => {
   200→  if (!props.evidence.id) return;
   201→  
   202→  try {
   203→    loadingTags.value = true;
   204→    
   205→    // Load tags grouped by status for better performance and organization
   206→    const teamId = authStore.currentTeam;
