      querySnapshot.forEach((doc) => {
        duplicates.push({
          id: doc.id,
          ...doc.data()
        })
      })

      return duplicates
    } catch (error) {
      console.error('Error finding duplicate tags:', error)
      throw error
    }
  }
}

