   111â†’<script setup>
   112â†’import { ref, computed } from 'vue';
   113â†’import FileUploadQueue from '../features/file-upload/components/FileUploadQueue.vue';
   114â†’import UploadDropzone from '../features/file-upload/components/UploadDropzone.vue';
   115â†’import FolderOptionsDialog from '../features/file-upload/components/FolderOptionsDialog.vue';
   116â†’import ProcessingProgressModal from '../features/file-upload/components/ProcessingProgressModal.vue';
   117â†’import CloudFileWarningModal from '../features/file-upload/components/CloudFileWarningModal.vue';
   118â†’import { useFileQueue } from '../features/file-upload/composables/useFileQueue.js';
   119â†’import { useFileDragDrop } from '../features/file-upload/composables/useFileDragDrop.js';
   120â†’import { useQueueDeduplication } from '../features/file-upload/composables/useQueueDeduplication.js';
   121â†’import { useFolderOptions } from '../features/file-upload/composables/useFolderOptions.js';
   122â†’import { useTimeBasedWarning } from '../composables/useTimeBasedWarning.js';
   123â†’import {
   124â†’  calculateCalibratedProcessingTime,
   125â†’  getStoredHardwarePerformanceFactor,
   126â†’} from '../features/file-upload/utils/hardwareCalibration.js';
   127â†’import { storage } from '../services/firebase.js';
   128â†’import { ref as storageRef, getDownloadURL, uploadBytesResumable } from 'firebase/storage';
   129â†’import { useAuthStore } from '../core/stores/auth.js';
   130â†’import { useLazyHashTooltip } from '../features/file-upload/composables/useLazyHashTooltip.js';
   131â†’import { useUploadLogger } from '../composables/useUploadLogger.js';
   132â†’import { useFileMetadata } from '../features/file-upload/composables/useFileMetadata.js';
   133â†’
   134â†’// Component configuration
   135â†’defineOptions({
   136â†’  name: 'FileUploadView',
   137â†’});
   138â†’
   139â†’// Template refs
   140â†’const dropzoneRef = ref(null);
   141â†’
   142â†’// Auth store
   143â†’const authStore = useAuthStore();
   144â†’
   145â†’// Hash tooltip system for cache management
   146â†’const { populateExistingHash } = useLazyHashTooltip();
   147â†’
   148â†’// Upload logging system
   149â†’const { logUploadEvent } = useUploadLogger();
   150â†’
   151â†’// File metadata system
   152â†’const { createMetadataRecord, generateMetadataHash } = useFileMetadata();
   153â†’
   154â†’// Composables
   155â†’const {
   156â†’  uploadQueue,
   157â†’  showSingleFileNotification,
   158â†’  singleFileNotification,
   159â†’  fileInput,
   160â†’  folderInput,
   161â†’  processingProgress,
   162â†’  isProcessingUIUpdate,
   163â†’  uiUpdateProgress,
   164â†’  uploadStatus,
   165â†’  triggerFileSelect,
   166â†’  triggerFolderSelect,
   167â†’  processSingleFile,
   168â†’  addFilesToQueue,
   169â†’  initializeQueueInstantly,
   170â†’  updateUploadQueue,
   171â†’  removeFromQueue,
   172â†’  showNotification,
   173â†’  setPhaseComplete,
   174â†’  resetUploadStatus,
   175â†’  updateUploadStatus,
   176â†’  updateFileStatus,
   177â†’  updateAllFilesToReady,
   178â†’} = useFileQueue();
   179â†’
   180â†’const {
   181â†’  isDragOver,
   182â†’  handleDragOver,
   183â†’  handleDragLeave,
   184â†’  handleDrop: baseHandleDrop,
   185â†’} = useFileDragDrop();
   186â†’
   187â†’const queueDeduplication = useQueueDeduplication();
   188â†’const { processFiles } = queueDeduplication;
   189â†’
   190â†’const {
   191â†’  showFolderOptions,
   192â†’  includeSubfolders,
   193â†’  subfolderCount,
   194â†’  isAnalyzing,
   195â†’  mainFolderAnalysis,
   196â†’  allFilesAnalysis,
   197â†’  totalDirectoryEntryCount,
   198â†’  // Timeout state
   199â†’  analysisTimedOut,
   200â†’  timeoutError,
   201â†’  currentProgressMessage,
   202â†’  skippedFolders,
   203â†’  // Progress tracking
   204â†’  mainFolderProgress,
   205â†’  allFilesProgress,
   206â†’  mainFolderComplete,
   207â†’  allFilesComplete,
   208â†’  isAnalyzingMainFolder,
   209â†’  isAnalyzingAllFiles,
   210â†’  processFolderEntry,
   211â†’  processFolderFiles,
   212â†’  confirmFolderOptions: baseConfirmFolderOptions,
   213â†’  cancelFolderUpload,
   214â†’} = useFolderOptions();
   215â†’
   216â†’const timeWarning = useTimeBasedWarning();
   217â†’
   218â†’// Upload state tracking
   219â†’const isStartingUpload = ref(false);
   220â†’const isPaused = ref(false);
   221â†’const pauseRequested = ref(false);
   222â†’const currentUploadIndex = ref(0);
   223â†’let uploadAbortController = null;
   224â†’
   225â†’// Session ID management
   226â†’const currentSessionId = ref(null);
   227â†’const getCurrentSessionId = () => {
   228â†’  if (!currentSessionId.value) {
   229â†’    currentSessionId.value = `upload_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
   230â†’  }
   231â†’  return currentSessionId.value;
   232â†’};
   233â†’
   234â†’// Simple hash calculation function (same as worker)
   235â†’const calculateFileHash = async (file) => {
   236â†’  try {
   237â†’    const buffer = await file.arrayBuffer();
   238â†’    const hashBuffer = await crypto.subtle.digest('SHA-256', buffer);
   239â†’    const hashArray = Array.from(new Uint8Array(hashBuffer));
   240â†’    const hash = hashArray.map((b) => b.toString(16).padStart(2, '0')).join('');
   241â†’
   242â†’    // Return standard SHA-256 hash of file content
   243â†’    return hash;
   244â†’  } catch (error) {
   245â†’    throw new Error(`Failed to generate hash for file ${file.name}: ${error.message}`);
   246â†’  }
   247â†’};
   248â†’
   249â†’// Simple upload helper functions
   250â†’const generateStoragePath = (fileHash, originalFileName) => {
   251â†’  const extension = originalFileName.split('.').pop();
   252â†’  return `teams/${authStore.currentTeam}/matters/general/uploads/${fileHash}.${extension}`;
   253â†’};
   254â†’
   255â†’const checkFileExists = async (fileHash, originalFileName) => {
   256â†’  try {
   257â†’    const storagePath = generateStoragePath(fileHash, originalFileName);
   258â†’    const storageReference = storageRef(storage, storagePath);
   259â†’    await getDownloadURL(storageReference);
   260â†’    return true;
   261â†’  } catch {
   262â†’    return false;
   263â†’  }
   264â†’};
   265â†’
   266â†’const uploadSingleFile = async (file, fileHash, originalFileName, abortSignal = null) => {
   267â†’  const storagePath = generateStoragePath(fileHash, originalFileName);
   268â†’  const storageReference = storageRef(storage, storagePath);
   269â†’
   270â†’  const uploadTask = uploadBytesResumable(storageReference, file, {
   271â†’    customMetadata: {
   272â†’      teamId: authStore.currentTeam,
   273â†’      userId: authStore.user.uid,
   274â†’      originalName: originalFileName,
   275â†’      hash: fileHash,
   276â†’    },
   277â†’  });
   278â†’
   279â†’  return new Promise((resolve, reject) => {
   280â†’    // Handle abort signal
   281â†’    if (abortSignal) {
   282â†’      abortSignal.addEventListener('abort', () => {
   283â†’        uploadTask.cancel();
   284â†’        reject(new Error('AbortError'));
   285â†’      });
   286â†’    }
   287â†’
   288â†’    uploadTask.on(
   289â†’      'state_changed',
   290â†’      () => {
   291â†’        // Check if aborted during progress
   292â†’        if (abortSignal && abortSignal.aborted) {
   293â†’          uploadTask.cancel();
   294â†’          reject(new Error('AbortError'));
   295â†’          return;
   296â†’        }
   297â†’        // Progress updates could be added here if needed
   298â†’      },
   299â†’      (error) => {
   300â†’        reject(error);
   301â†’      },
   302â†’      async () => {
   303â†’        // Upload completed successfully
   304â†’        try {
   305â†’          const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
   306â†’          resolve({ success: true, downloadURL });
   307â†’        } catch (error) {
   308â†’          reject(error);
   309â†’        }
   310â†’      }
   311â†’    );
   312â†’  });
   313â†’};
   314â†’
   315â†’// Connect time monitoring to deduplication processing
   316â†’queueDeduplication.setTimeMonitoringCallback({
   317â†’  onProcessingStart: () => {
   318â†’    // Time monitoring will be started when we have an estimate from folder analysis
   319â†’  },
   320â†’  onProcessingComplete: () => {
   321â†’    timeWarning.resetMonitoring();
   322â†’    queueDeduplication.terminateWorker();
   323â†’  },
   324â†’  onProcessingError: (error) => {
   325â†’    timeWarning.resetMonitoring();
   326â†’    queueDeduplication.terminateWorker();
   327â†’    console.log('File processing error - time monitoring reset and workers terminated:', error);
   328â†’  },
   329â†’  onProcessingAborted: () => {
   330â†’    queueDeduplication.terminateWorker();
   331â†’    console.log('File processing aborted - workers terminated');
   332â†’  },
   333â†’});
   334â†’
   335â†’// Legacy: Complex cleanup function replaced by simple page refresh
   336â†’
   337â†’// Enhanced clearQueue that uses comprehensive cleanup
   338â†’const clearQueue = () => {
   339â†’  // Simple and reliable: just refresh the page
   340â†’  console.log('ğŸ”„ Clear All: Refreshing page to reset all state');
   341â†’  window.location.reload();
   342â†’};
   343â†’
   344â†’// Integrate processFiles with updateUploadQueue with safety filtering
   345â†’const processFilesWithQueue = async (files) => {
   346â†’  const processId = Math.random().toString(36).substr(2, 9);
   347â†’
   348â†’  // Check if analysis has been aborted before processing
   349â†’  if (analysisTimedOut.value) {
   350â†’    console.log('Skipping file processing - analysis was aborted');
   351â†’    return;
   352â†’  }
   353â†’
   354â†’  // Additional safety check - if queue is empty, processing was likely cancelled
   355â†’  if (!files || files.length === 0) {
   356â†’    console.log('Skipping file processing - no files to process');
   357â†’    return;
   358â†’  }
   359â†’
   360â†’  // Start time monitoring if not already started and we have files to process
   361â†’  if (files.length > 0 && !timeWarning.startTime.value) {
   362â†’    const folderAnalysis = allFilesAnalysis.value || mainFolderAnalysis.value;
   363â†’    let estimatedTime = 0;
   364â†’
   365â†’    if (folderAnalysis && folderAnalysis.timeMs && folderAnalysis.timeMs > 0) {
   366â†’      // Use existing folder analysis estimate (already hardware-calibrated)
   367â†’      estimatedTime = folderAnalysis.timeMs;
   368â†’    } else {
   369â†’      // Generate hardware-calibrated estimate on-the-fly for files without folder analysis
   370â†’      // No folder analysis available, generating hardware-calibrated estimate for file processing
   371â†’
   372â†’      // Get stored hardware performance factor or use baseline
   373â†’      let hardwarePerformanceFactor = getStoredHardwarePerformanceFactor();
   374â†’      if (!hardwarePerformanceFactor || hardwarePerformanceFactor <= 0) {
   375â†’        // Use baseline H-factor for new users (1.61 files/ms)
   376â†’        hardwarePerformanceFactor = 1.61;
   377â†’      }
   378â†’
   379â†’      // Create file analysis data for calibrated estimation
   380â†’      const sizeMap = new Map();
   381â†’      files.forEach((file) => {
   382â†’        const size = file.size || 0;
   383â†’        sizeMap.set(size, (sizeMap.get(size) || 0) + 1);
   384â†’      });
   385â†’      const duplicateCandidates = files.filter((file) => sizeMap.get(file.size || 0) > 1);
   386â†’      const duplicateCandidatesSizeMB =
   387â†’        duplicateCandidates.reduce((sum, file) => sum + (file.size || 0), 0) / (1024 * 1024);
   388â†’
   389â†’      const folderData = {
   390â†’        totalFiles: files.length,
   391â†’        duplicateCandidates: duplicateCandidates.length,
   392â†’        duplicateCandidatesSizeMB: Math.round(duplicateCandidatesSizeMB * 10) / 10,
   393â†’        avgDirectoryDepth: 2.5, // Default assumption for direct file processing
   394â†’        totalDirectoryCount: 1,
   395â†’      };
   396â†’
   397â†’      // Generate hardware-calibrated estimate
   398â†’      const calibratedPrediction = calculateCalibratedProcessingTime(
   399â†’        folderData,
   400â†’        hardwarePerformanceFactor
   401â†’      );
   402â†’      estimatedTime = calibratedPrediction.totalTimeMs;
   403â†’    }
   404â†’
   405â†’    if (estimatedTime > 0) {
   406â†’      timeWarning.startMonitoring(estimatedTime);
   407â†’    } else {
   408â†’      console.warn('Time monitoring not started: Unable to generate hardware-calibrated estimate');
   409â†’    }
   410â†’  }
   411â†’
   412â†’  // Check if processing was aborted after time monitoring setup
   413â†’  if (analysisTimedOut.value) {
   414â†’    console.log('Aborting file processing - analysis was cancelled after time monitoring setup');
   415â†’    return;
   416â†’  }
   417â†’
   418â†’  // Double-check safety filter: exclude any files from skipped folders
   419â†’  if (skippedFolders.value && skippedFolders.value.length > 0) {
   420â†’    const originalCount = files.length;
   421â†’    const safeFiles = files.filter((file) => {
   422â†’      const filePath = file.path || file.webkitRelativePath || file.name;
   423â†’      const isInSkippedFolder = skippedFolders.value.some((skippedPath) => {
   424â†’        const normalizedFilePath = filePath.replace(/\\/g, '/').toLowerCase();
   425â†’        const normalizedSkippedPath = skippedPath.replace(/\\/g, '/').toLowerCase();
   426â†’        return normalizedFilePath.startsWith(normalizedSkippedPath);
   427â†’      });
   428â†’
   429â†’      if (isInSkippedFolder) {
   430â†’        console.log(`Upload safety filter: excluding ${filePath} from skipped folder`);
   431â†’        return false;
   432â†’      }
   433â†’      return true;
   434â†’    });
   435â†’
   436â†’    if (safeFiles.length !== originalCount) {
   437â†’      console.log(
   438â†’        `Upload safety filter: excluded ${originalCount - safeFiles.length} files from ${skippedFolders.value.length} skipped folders`
   439â†’      );
   440â†’    }
   441â†’
   442â†’    // Final check before actually processing files
   443â†’    if (analysisTimedOut.value) {
   444â†’      console.log(`âŒ [${processId}] EXIT EARLY: aborted during file preparation`);
   445â†’      return;
   446â†’    }
   447â†’
   448â†’    try {
   449â†’      // Final abort check immediately before processFiles call
   450â†’      if (analysisTimedOut.value) {
   451â†’        console.log(`âŒ [${processId}] EXIT EARLY: aborted right before processFiles call`);
   452â†’        return;
   453â†’      }
   454â†’
   455â†’      await processFiles(safeFiles, updateUploadQueue);
   456â†’
   457â†’      // Check if processing was aborted after processFiles completed
   458â†’      if (analysisTimedOut.value) {
   459â†’        console.log(
   460â†’          `âŒ [${processId}] EXIT ABORT: processing aborted after completion - skipping cleanup`
   461â†’        );
   462â†’        return;
   463â†’      }
   464â†’
   465â†’      // Processing completed successfully - perform cleanup
   466â†’      updateAllFilesToReady(); // Update all ready files to show blue dots
   467â†’      timeWarning.resetMonitoring();
   468â†’      queueDeduplication.clearTimeMonitoringCallback();
   469â†’    } catch (error) {
   470â†’      console.error('Error during file processing:', error);
   471â†’
   472â†’      // Check if error was due to abort
   473â†’      if (analysisTimedOut.value) {
   474â†’        console.log(`âŒ [${processId}] EXIT ERROR ABORT: processing aborted during error handling`);
   475â†’        return;
   476â†’      }
   477â†’
   478â†’      // Cleanup on error as well
   479â†’      timeWarning.resetMonitoring();
   480â†’      queueDeduplication.clearTimeMonitoringCallback();
   481â†’      throw error; // Re-throw to maintain error handling
   482â†’    }
   483â†’  } else {
   484â†’    // Final check before actually processing files (no folder filtering path)
   485â†’    if (analysisTimedOut.value) {
   486â†’      console.log('Aborting file processing - analysis was cancelled during preparation');
   487â†’      return;
   488â†’    }
   489â†’
   490â†’    try {
   491â†’      // Final abort check immediately before processFiles call
   492â†’      if (analysisTimedOut.value) {
   493â†’        console.log(`âŒ [${processId}] EXIT EARLY: aborted right before processFiles call`);
   494â†’        return;
   495â†’      }
   496â†’
   497â†’      await processFiles(files, updateUploadQueue);
   498â†’
   499â†’      // Check if processing was aborted after processFiles completed
   500â†’      if (analysisTimedOut.value) {
   501â†’        console.log(
   502â†’          `âŒ [${processId}] EXIT ABORT: processing aborted after completion - skipping cleanup`
   503â†’        );
   504â†’        return;
   505â†’      }
   506â†’
   507â†’      // Processing completed successfully - perform cleanup
   508â†’      updateAllFilesToReady(); // Update all ready files to show blue dots
   509â†’      timeWarning.resetMonitoring();
   510â†’      queueDeduplication.clearTimeMonitoringCallback();
   511â†’    } catch (error) {
   512â†’      console.error('Error during file processing:', error);
   513â†’
   514â†’      // Check if error was due to abort
   515â†’      if (analysisTimedOut.value) {
   516â†’        console.log(`âŒ [${processId}] EXIT ERROR ABORT: processing aborted during error handling`);
   517â†’        return;
   518â†’      }
   519â†’
   520â†’      // Cleanup on error as well
   521â†’      timeWarning.resetMonitoring();
   522â†’      queueDeduplication.clearTimeMonitoringCallback();
   523â†’      throw error; // Re-throw to maintain error handling
   524â†’    }
   525â†’  }
   526â†’
   527â†’  // ğŸ” DEBUG: Log AsyncTracker state at successful exit
