    29â†’ *   autoApproved: boolean,    // Whether auto-approved by confidence threshold
    30â†’ *   createdAt: timestamp,     // When tag was generated
    31â†’ *   reviewedAt: timestamp,    // When tag was manually reviewed (if applicable)
    32â†’ *   source: string,          // 'ai' | 'manual' | 'bulk_import'
