    this.tagService = new TagSubcollectionService(teamId);
    this.batchSize = 10; // Process 10 documents at a time
  }

  /**
   * Get the current team ID from auth store if not provided in constructor
   * @returns {string} - Current team ID
   */
  getTeamId() {
