   270  const uploadTask = uploadBytesResumable(storageReference, file, {
   271    customMetadata: {
   272      teamId: authStore.currentTeam,
   273      userId: authStore.user.uid,
   274      originalName: originalFileName,
   275      hash: fileHash,
   276    },
   277  });
   278
   279  return new Promise((resolve, reject) => {
   280    // Handle abort signal
   281    if (abortSignal) {
   282      abortSignal.addEventListener('abort', () => {
   283        uploadTask.cancel();
   284        reject(new Error('AbortError'));
   285      });
   286    }
   287
   288    uploadTask.on(
   289      'state_changed',
   290      () => {
   291        // Check if aborted during progress
   292        if (abortSignal && abortSignal.aborted) {
   293          uploadTask.cancel();
   294          reject(new Error('AbortError'));
   295          return;
   296        }
   297        // Progress updates could be added here if needed
   298      },
   299      (error) => {
   300        reject(error);
   301      },
   302      async () => {
   303        // Upload completed successfully
   304        try {
   305          const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
   306          resolve({ success: true, downloadURL });
   307        } catch (error) {
   308          reject(error);
   309        }
   310      }
   311    );
