   344// Integrate processFiles with updateUploadQueue with safety filtering
   345const processFilesWithQueue = async (files) => {
   346  const processId = Math.random().toString(36).substr(2, 9);
   347
   348  // Check if analysis has been aborted before processing
   349  if (analysisTimedOut.value) {
   350    console.log('Skipping file processing - analysis was aborted');
   351    return;
   352  }
   353
   354  // Additional safety check - if queue is empty, processing was likely cancelled
   355  if (!files || files.length === 0) {
   356    console.log('Skipping file processing - no files to process');
   357    return;
   358  }
   359
   360  // Start time monitoring if not already started and we have files to process
   361  if (files.length > 0 && !timeWarning.startTime.value) {
   362    const folderAnalysis = allFilesAnalysis.value || mainFolderAnalysis.value;
   363    let estimatedTime = 0;
   364
   365    if (folderAnalysis && folderAnalysis.timeMs && folderAnalysis.timeMs > 0) {
   366      // Use existing folder analysis estimate (already hardware-calibrated)
   367      estimatedTime = folderAnalysis.timeMs;
   368    } else {
   369      // Generate hardware-calibrated estimate on-the-fly for files without folder analysis
   370      // No folder analysis available, generating hardware-calibrated estimate for file processing
   371
   372      // Get stored hardware performance factor or use baseline
   373      let hardwarePerformanceFactor = getStoredHardwarePerformanceFactor();
   374      if (!hardwarePerformanceFactor || hardwarePerformanceFactor <= 0) {
   375        // Use baseline H-factor for new users (1.61 files/ms)
   376        hardwarePerformanceFactor = 1.61;
   377      }
   378
   379      // Create file analysis data for calibrated estimation
   380      const sizeMap = new Map();
   381      files.forEach((file) => {
   382        const size = file.size || 0;
   383        sizeMap.set(size, (sizeMap.get(size) || 0) + 1);
   384      });
   385      const duplicateCandidates = files.filter((file) => sizeMap.get(file.size || 0) > 1);
   386      const duplicateCandidatesSizeMB =
   387        duplicateCandidates.reduce((sum, file) => sum + (file.size || 0), 0) / (1024 * 1024);
   388
   389      const folderData = {
   390        totalFiles: files.length,
   391        duplicateCandidates: duplicateCandidates.length,
   392        duplicateCandidatesSizeMB: Math.round(duplicateCandidatesSizeMB * 10) / 10,
   393        avgDirectoryDepth: 2.5, // Default assumption for direct file processing
   394        totalDirectoryCount: 1,
   395      };
   396
   397      // Generate hardware-calibrated estimate
   398      const calibratedPrediction = calculateCalibratedProcessingTime(
   399        folderData,
   400        hardwarePerformanceFactor
   401      );
   402      estimatedTime = calibratedPrediction.totalTimeMs;
   403    }
   404
   405    if (estimatedTime > 0) {
   406      timeWarning.startMonitoring(estimatedTime);
   407    } else {
   408      console.warn('Time monitoring not started: Unable to generate hardware-calibrated estimate');
   409    }
   410  }
   411
   412  // Check if processing was aborted after time monitoring setup
   413  if (analysisTimedOut.value) {
   414    console.log('Aborting file processing - analysis was cancelled after time monitoring setup');
   415    return;
   416  }
   417
   418  // Double-check safety filter: exclude any files from skipped folders
   419  if (skippedFolders.value && skippedFolders.value.length > 0) {
   420    const originalCount = files.length;
   421    const safeFiles = files.filter((file) => {
   422      const filePath = file.path || file.webkitRelativePath || file.name;
   423      const isInSkippedFolder = skippedFolders.value.some((skippedPath) => {
   424        const normalizedFilePath = filePath.replace(/\\/g, '/').toLowerCase();
   425        const normalizedSkippedPath = skippedPath.replace(/\\/g, '/').toLowerCase();
   426        return normalizedFilePath.startsWith(normalizedSkippedPath);
   427      });
   428
   429      if (isInSkippedFolder) {
   430        console.log(`Upload safety filter: excluding ${filePath} from skipped folder`);
   431        return false;
   432      }
   433      return true;
   434    });
   435
   436    if (safeFiles.length !== originalCount) {
   437      console.log(
   438        `Upload safety filter: excluded ${originalCount - safeFiles.length} files from ${skippedFolders.value.length} skipped folders`
   439      );
   440    }
   441
   442    // Final check before actually processing files
   443    if (analysisTimedOut.value) {
   444      console.log(` [${processId}] EXIT EARLY: aborted during file preparation`);
   445      return;
   446    }
   447
   448    try {
   449      // Final abort check immediately before processFiles call
   450      if (analysisTimedOut.value) {
   451        console.log(` [${processId}] EXIT EARLY: aborted right before processFiles call`);
   452        return;
   453      }
