   465      // Processing completed successfully - perform cleanup
   466      updateAllFilesToReady(); // Update all ready files to show blue dots
   467      timeWarning.resetMonitoring();
   468      queueDeduplication.clearTimeMonitoringCallback();
   469    } catch (error) {
