   496
   497      await processFiles(files, updateUploadQueue);
   498
   499      // Check if processing was aborted after processFiles completed
   500      if (analysisTimedOut.value) {
   501        console.log(
   502          ` [${processId}] EXIT ABORT: processing aborted after completion - skipping cleanup`
   503        );
   504        return;
