   506
   507      // Processing completed successfully - perform cleanup
   508      updateAllFilesToReady(); // Update all ready files to show blue dots
   509      timeWarning.resetMonitoring();
   510      queueDeduplication.clearTimeMonitoringCallback();
   511    } catch (error) {
   512      console.error('Error during file processing:', error);
