    82
    83  const minutes = Math.floor(estimatedSeconds / 60);
    84  const remainingSeconds = estimatedSeconds % 60;
    85
    86  if (minutes > 0) {
    87    return `${minutes}m ${remainingSeconds}s`;
    88  }
    89  return `${estimatedSeconds}s`;
    90});
