   298    default:
   299      return 'mdi-alert-circle';
   300  }
   301});
   302
   303const errorTitle = computed(() => {
   304  if (!props.error) return 'Error';
   305
   306  switch (props.error.type) {
   307    case 'timeout':
   308      return 'Processing Timeout';
   309    case 'worker-error':
   310      return 'Web Worker Error';
   311    case 'memory-error':
   312      return 'Memory Error';
   313    case 'validation-error':
   314      return 'File Validation Error';
   315    case 'network-error':
   316      return 'Network Error';
   317    default:
   318      return 'Processing Error';
   319  }
   320});
   321
   322const errorSeverityClass = computed(() => {
   323  if (!props.error) return '';
   324
   325  switch (props.error.severity) {
   326    case 'critical':
   327      return 'text-red-darken-2';
   328    case 'warning':
   329      return 'text-orange-darken-2';
   330    default:
   331      return 'text-red-darken-1';
   332  }
   333});
   334
   335const errorSuggestions = computed(() => {
   336  if (!props.error) return [];
   337
   338  switch (props.error.type) {
   339    case 'timeout':
   340      return [
   341        'Try processing fewer files at once',
   342        'Check your system performance',
   343        'Use fallback mode for large file sets',
   344      ];
   345    case 'worker-error':
   346      return [
   347        'Try using fallback mode',
   348        'Restart your browser if the problem persists',
   349        'Check for browser compatibility issues',
   350      ];
   351    case 'memory-error':
   352      return [
   353        'Process fewer files at once',
   354        'Close other browser tabs',
   355        'Use fallback mode for better memory management',
   356      ];
   357    case 'validation-error':
   358      return [
   359        'Check that all selected items are valid files',
   360        'Remove any corrupted or inaccessible files',
   361        'Try selecting files individually',
   362      ];
   363    default:
   364      return [
   365        'Try using fallback mode',
   366        'Check your internet connection',
   367        'Contact support if the problem persists',
   368      ];
   369  }
   370});
   371
   372// Helper functions
   373const formatTime = (milliseconds) => {
   374  if (!milliseconds) return '';
   375
   376  const seconds = Math.ceil(milliseconds / 1000);
   377
   378  if (seconds < 60) {
   379    return `${seconds}s`;
   380  } else if (seconds < 3600) {
   381    const minutes = Math.floor(seconds / 60);
   382    const remainingSeconds = seconds % 60;
   383    return remainingSeconds > 0 ? `${minutes}m ${remainingSeconds}s` : `${minutes}m`;
   384  } else {
   385    const hours = Math.floor(seconds / 3600);
   386    const minutes = Math.floor((seconds % 3600) / 60);
   387    return minutes > 0 ? `${hours}h ${minutes}m` : `${hours}h`;
   388  }
   389};
