   173// Computed properties
   174const totalFiles = computed(
   175  () => props.results.successful.length + props.results.failed.length + props.results.skipped.length
   176);
   177
   178const hasErrors = computed(() => props.results.failed.length > 0);
   179
   180const totalBytesUploaded = computed(() => {
   181  // Calculate from successful uploads
   182  let totalBytes = 0;
   183
   184  props.results.successful.forEach(({ fileInfo }) => {
   185    if (fileInfo && fileInfo.file) {
   186      totalBytes += fileInfo.file.size || 0;
   187    }
   188  });
   189
   190  return totalBytes;
   191});
   192
   193const formattedDuration = computed(() => {
   194  const seconds = props.duration / 1000;
   195
   196  if (seconds < 60) {
   197    return `${Math.ceil(seconds)}s`;
   198  } else if (seconds < 3600) {
   199    const minutes = Math.floor(seconds / 60);
   200    const remainingSeconds = Math.ceil(seconds % 60);
