   277  const k = 1024;
   278  const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
   279  const i = Math.floor(Math.log(bytes) / Math.log(k));
   280  return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
