   279// Populate existing hashes when component mounts or props change
   280const populateExistingHashes = () => {
   281  props.files.forEach((file) => {
   282    if (file.hash) {
   283      populateExistingHash(file.id || file.name, file.hash);
   284    }
   285  });
   286};
