   234// Simple hash calculation function (same as worker)
   235const calculateFileHash = async (file) => {
   236  try {
   237    const buffer = await file.arrayBuffer();
   238    const hashBuffer = await crypto.subtle.digest('SHA-256', buffer);
   239    const hashArray = Array.from(new Uint8Array(hashBuffer));
   240    const hash = hashArray.map((b) => b.toString(16).padStart(2, '0')).join('');
   241
   242    // Return standard SHA-256 hash of file content
   243    return hash;
   244  } catch (error) {
   245    throw new Error(`Failed to generate hash for file ${file.name}: ${error.message}`);
   246  }
   247};
