   519
   520      // Cleanup on error as well
   521      timeWarning.resetMonitoring();
   522      queueDeduplication.clearTimeMonitoringCallback();
   523      throw error; // Re-throw to maintain error handling
   524    }
   525  }
   526
   527  //  DEBUG: Log AsyncTracker state at successful exit
   528  if (window.__asyncTracker) {
