   658// Resumable upload loop function
   659const continueUpload = async () => {
   660  try {
   661    console.log('Continuing upload process...');
   662
   663    // Get all files from upload queue (including duplicates for processing)
   664    const filesToProcess = uploadQueue.value;
   665
   666    if (filesToProcess.length === 0) {
   667      showNotification('No files to process', 'info');
   668      updateUploadStatus('complete');
   669      return;
   670    }
   671
   672    // Start from current index or beginning
   673    const startIndex = uploadStatus.value.currentUploadIndex || 0;
   674    console.log(`Continuing from file ${startIndex + 1} of ${filesToProcess.length}`);
   675
   676    // Process files from current index
   677    for (let i = startIndex; i < filesToProcess.length; i++) {
   678      // Check if pause was requested before processing next file
   679      if (pauseRequested.value) {
   680        console.log(`Pause requested at file ${i + 1}. Pausing...`);
   681        updateUploadStatus('setUploadIndex', i); // Save current position
   682        updateUploadStatus('pause');
   683        isPaused.value = true;
   684        pauseRequested.value = false;
   685        return;
   686      }
   687
   688      const queueFile = filesToProcess[i];
   689      updateUploadStatus('setUploadIndex', i); // Update current position
   690
   691      // Handle duplicate files - skip upload but log and save metadata
   692      if (queueFile.isDuplicate) {
   693        console.log(`Processing duplicate file: ${queueFile.name}`);
   694        updateUploadStatus('currentFile', queueFile.name, 'processing_duplicate');
   695        updateFileStatus(queueFile, 'skipped');
   696
   697        try {
   698          // Log skipped event for duplicate file
   699          const metadataHash = await generateMetadataHash(
   700            queueFile.name,
   701            queueFile.lastModified,
   702            queueFile.hash
   703          );
   704          await logUploadEvent({
   705            eventType: 'upload_skipped_metadata_recorded',
   706            fileName: queueFile.name,
   707            fileHash: queueFile.hash,
   708            metadataHash: metadataHash,
   709          });
   710
   711          // Create metadata record for duplicate (hash deduplication)
   712          await createMetadataRecord({
   713            originalName: queueFile.name,
   714            lastModified: queueFile.lastModified,
   715            fileHash: queueFile.hash,
   716            sessionId: getCurrentSessionId(),
   717            originalPath: queueFile.path, // webkitRelativePath for folder uploads
