   723          console.error(`Failed to process duplicate file ${queueFile.name}:`, error);
   724          updateFileStatus(queueFile, 'failed');
   725          updateUploadStatus('failed');
   726        }
   727
   728        continue; // Skip to next file
   729      }
   730
   731      try {
   732        // Create abort controller for this upload
   733        uploadAbortController = new AbortController();
   734
   735        // Calculate hash for the file (on-demand during upload)
