     1import { db } from '../services/firebase.js';
     2import { doc, setDoc, getDoc, serverTimestamp } from 'firebase/firestore';
     3import { useAuthStore } from '../stores/auth.js';
     4import { updateFolderPaths } from '../utils/folderPathUtils.js';
     5
     6export function useFileMetadata() {
     7  const authStore = useAuthStore();
     8
     9  /**
    10   * Generate metadata hash from file metadata
    11   * Uses concatenated string: originalName|lastModified|fileHash
    12   * @param {string} originalName - Original filename
    13   * @param {number} lastModified - File's last modified timestamp
    14   * @param {string} fileHash - Content hash of the file
    15   * @returns {Promise<string>} - SHA-256 hash of metadata string
    16   */
    17  const generateMetadataHash = async (originalName, lastModified, fileHash) => {
    18    try {
    19      // Create deterministic concatenated string with pipe delimiters
    20      const metadataString = `${originalName}|${lastModified}|${fileHash}`;
    21
    22      // Generate SHA-256 hash of the metadata string
    23      const buffer = new TextEncoder().encode(metadataString);
    24      const hashBuffer = await crypto.subtle.digest('SHA-256', buffer);
    25      const hashArray = Array.from(new Uint8Array(hashBuffer));
    26      const metadataHash = hashArray.map((b) => b.toString(16).padStart(2, '0')).join('');
    27
    28      return metadataHash;
    29    } catch (error) {
    30      console.error('Failed to generate metadata hash:', error);
    31      throw new Error(`Failed to generate metadata hash: ${error.message}`);
    32    }
    33  };
    34
    35  /**
    36   * Create a file metadata record in Firestore
    37   * @param {Object} fileData - File metadata information
    38   * @param {string} fileData.originalName - Original filename
    39   * @param {number} fileData.lastModified - File's last modified timestamp
    40   * @param {string} fileData.fileHash - Content hash of the file
    41   * @param {string} [fileData.originalPath] - Full relative path from folder upload (used to extract folderPath)
    42   * @returns {Promise<string>} - The metadata hash used as document ID
    43   */
    44  const createMetadataRecord = async (fileData) => {
    45    try {
    46      const { originalName, lastModified, fileHash, originalPath } = fileData;
    47
    48      if (!originalName || !lastModified || !fileHash) {
    49        throw new Error(
    50          'Missing required metadata fields: originalName, lastModified, or fileHash'
    51        );
    52      }
    53
    54      const teamId = authStore.currentTeam;
    55      if (!teamId) {
    56        throw new Error('No team ID available for metadata record');
    57      }
    58
    59      // Generate metadata hash for document ID
    60      const metadataHash = await generateMetadataHash(originalName, lastModified, fileHash);
