     1import { ref, onUnmounted } from 'vue';
     2import { useAsyncRegistry } from './useAsyncRegistry';
     3
     4/**
     5 * Generic Web Worker communication composable
     6 * Provides a clean API for worker lifecycle management and message passing
     7 */
     8export function useWebWorker(workerPath) {
     9  // Reactive state
    10  const isWorkerSupported = ref(typeof Worker !== 'undefined');
    11  const worker = ref(null);
    12  const isWorkerReady = ref(false);
    13  const workerError = ref(null);
    14  const isWorkerHealthy = ref(true);
    15
    16  // Message handling
    17  const pendingMessages = new Map(); // batchId -> { resolve, reject, timeout, startTime }
    18  const messageListeners = new Map(); // messageType -> callback[]
    19
    20  // Health monitoring
    21  let healthCheckInterval = null;
    22  let lastHealthCheck = null;
    23  let consecutiveHealthFailures = 0;
    24  const MAX_HEALTH_FAILURES = 3;
    25  const HEALTH_CHECK_INTERVAL = 15000; // 15 seconds - less aggressive
    26  const HEALTH_CHECK_TIMEOUT = 8000; // 8 seconds - more time for response
    27  const HEALTH_CHECK_GRACE_PERIOD = 30000; // 30 seconds before first health check
    28
    29  // Registry integration
    30  const registry = useAsyncRegistry();
    31  let workerRegistryId = null;
    32  let healthCheckRegistryId = null;
    33
    34  // Initialize worker
    35  const initializeWorker = () => {
    36    if (!isWorkerSupported.value) {
    37      workerError.value = new Error('Web Workers are not supported in this browser');
    38      return false;
    39    }
    40
    41    try {
    42      worker.value = new Worker(new URL(workerPath, import.meta.url));
    43
    44      worker.value.onmessage = (event) => {
    45        handleWorkerMessage(event.data);
    46      };
    47
    48      worker.value.onerror = (error) => {
    49        workerError.value = error;
    50        isWorkerReady.value = false;
    51        isWorkerHealthy.value = false;
    52
    53        console.error('Worker error:', error);
    54
    55        // Stop health checks
    56        stopHealthChecking();
    57
    58        // Reject all pending messages
    59        for (const [batchId, { reject }] of pendingMessages) {
    60          reject(new Error(`Worker error: ${error.message}`));
    61          pendingMessages.delete(batchId);
    62        }
    63      };
    64
    65      worker.value.onmessageerror = (error) => {
    66        workerError.value = new Error(`Message error: ${error}`);
    67      };
    68
    69      isWorkerReady.value = true;
    70      isWorkerHealthy.value = true;
    71      workerError.value = null;
    72      consecutiveHealthFailures = 0;
    73
    74      // Register worker with registry
    75      workerRegistryId = registry.register(
    76        registry.generateId('worker'),
    77        'worker',
    78        () => {
    79          // The proper cleanup is handled by terminateWorker()
    80          // This cleanup function is called by the registry's global cleanup
    81          if (worker.value) {
    82            worker.value.terminate();
    83            worker.value = null;
    84            isWorkerReady.value = false;
    85            isWorkerHealthy.value = false;
    86          }
    87          // Note: We can't unregister here due to circular reference
    88          // The registry will handle removing this entry after cleanup executes
    89        },
    90        {
    91          component: 'WebWorker',
    92          workerPath,
    93          initialized: Date.now(),
    94        }
    95      );
    96
    97      if (import.meta.env.DEV) {
    98        console.debug('[WebWorker] Main worker registered with ID:', workerRegistryId);
    99      }
   100
   101      // Start health monitoring after grace period
   102      setTimeout(() => {
   103        if (isWorkerReady.value) {
   104          startHealthChecking();
   105        }
   106      }, HEALTH_CHECK_GRACE_PERIOD);
   107
   108      return true;
   109    } catch (error) {
   110      workerError.value = error;
   111      isWorkerReady.value = false;
   112      isWorkerHealthy.value = false;
   113      return false;
   114    }
   115  };
   116
   117  // Health check functionality
   118  const startHealthChecking = () => {
   119    if (healthCheckInterval) {
   120      clearInterval(healthCheckInterval);
   121      if (healthCheckRegistryId) {
   122        registry.unregister(healthCheckRegistryId);
   123      }
   124    }
   125
   126    healthCheckInterval = setInterval(async () => {
   127      try {
   128        await performHealthCheck();
   129      } catch (error) {
   130        console.warn('Health check failed:', error.message);
   131        handleHealthCheckFailure();
   132      }
   133    }, HEALTH_CHECK_INTERVAL);
   134
   135    // Register health check interval with registry
   136    healthCheckRegistryId = registry.register(
   137      registry.generateId('health-check'),
   138      'health-monitor',
   139      () => {
   140        if (healthCheckInterval) {
   141          clearInterval(healthCheckInterval);
   142          healthCheckInterval = null;
   143        }
   144      },
   145      {
   146        component: 'WebWorker',
   147        interval: HEALTH_CHECK_INTERVAL,
   148        purpose: 'worker-health-monitoring',
   149      }
   150    );
   151
   152    if (import.meta.env.DEV) {
   153      console.debug('[WebWorker] Health monitor registered with ID:', healthCheckRegistryId);
   154    }
   155  };
   156
   157  const stopHealthChecking = () => {
   158    if (import.meta.env.DEV) {
   159      console.debug(
   160        '[WebWorker] Stopping health monitoring. Interval exists:',
   161        !!healthCheckInterval,
   162        'Registry ID exists:',
   163        !!healthCheckRegistryId
   164      );
   165    }
   166
   167    if (healthCheckInterval) {
   168      clearInterval(healthCheckInterval);
   169      healthCheckInterval = null;
   170
   171      if (import.meta.env.DEV) {
   172        console.debug('[WebWorker] Health check interval cleared');
   173      }
   174    }
   175
   176    if (healthCheckRegistryId) {
   177      if (import.meta.env.DEV) {
   178        console.debug('[WebWorker] Unregistering health monitor with ID:', healthCheckRegistryId);
   179      }
   180
   181      registry.unregister(healthCheckRegistryId);
   182      healthCheckRegistryId = null;
   183
   184      if (import.meta.env.DEV) {
   185        console.debug('[WebWorker] Health monitor unregistered successfully');
   186      }
   187    }
   188  };
   189
   190  const performHealthCheck = () => {
   191    return new Promise((resolve, reject) => {
   192      if (!isWorkerReady.value || !worker.value) {
   193        reject(new Error('Worker not ready for health check'));
   194        return;
   195      }
   196
   197      const healthCheckId = `health_${Date.now()}`;
   198      const timeout = setTimeout(() => {
   199        reject(new Error('Health check timeout'));
   200      }, HEALTH_CHECK_TIMEOUT);
   201
   202      // Listen for health check response
   203      const healthResponseHandler = (data) => {
   204        if (data.type === 'HEALTH_CHECK_RESPONSE' && data.batchId === healthCheckId) {
   205          clearTimeout(timeout);
   206          lastHealthCheck = Date.now();
   207          consecutiveHealthFailures = 0;
   208          isWorkerHealthy.value = true;
   209          resolve();
   210        }
   211      };
   212
   213      const unsubscribe = addMessageListener('HEALTH_CHECK_RESPONSE', healthResponseHandler);
   214
   215      // Clean up listener after timeout
   216      setTimeout(() => {
   217        unsubscribe();
   218      }, HEALTH_CHECK_TIMEOUT + 1000);
   219
   220      // Send health check
   221      worker.value.postMessage({
   222        type: 'HEALTH_CHECK',
   223        batchId: healthCheckId,
   224        timestamp: Date.now(),
   225      });
   226    });
   227  };
   228
   229  const handleHealthCheckFailure = () => {
   230    consecutiveHealthFailures++;
   231    console.warn(
   232      `Worker health check failed (${consecutiveHealthFailures}/${MAX_HEALTH_FAILURES})`
   233    );
   234
   235    // Only mark unhealthy after MAX_HEALTH_FAILURES consecutive failures
   236    if (consecutiveHealthFailures >= MAX_HEALTH_FAILURES) {
   237      console.error(
   238        `Worker failed ${MAX_HEALTH_FAILURES} consecutive health checks, marking as unhealthy`
   239      );
   240      isWorkerHealthy.value = false;
   241    } else {
   242      // Keep worker healthy until max failures reached
   243      console.info(
   244        `Worker still healthy despite health check failure (${consecutiveHealthFailures}/${MAX_HEALTH_FAILURES})`
   245      );
   246    }
   247  };
   248
   249  // Handle incoming messages from worker
   250  const handleWorkerMessage = (data) => {
   251    const { type, batchId } = data;
   252
   253    // Handle completion/error messages that resolve promises
   254    if (batchId && pendingMessages.has(batchId)) {
   255      const { resolve, reject, timeout } = pendingMessages.get(batchId);
   256
   257      if (type === 'PROCESSING_COMPLETE') {
   258        clearTimeout(timeout);
   259        const { startTime } = pendingMessages.get(batchId);
   260        const duration = Date.now() - startTime;
   261        console.debug(`Worker operation completed in ${duration}ms`);
   262        pendingMessages.delete(batchId);
   263        resolve(data.result);
   264        return;
   265      }
   266
   267      if (type === 'ERROR') {
   268        clearTimeout(timeout);
   269        const { startTime } = pendingMessages.get(batchId);
   270        const duration = Date.now() - startTime;
   271        console.error(`Worker operation failed after ${duration}ms:`, data.error);
