      }
    };
  };

  // Remove message listener
  const removeMessageListener = (messageType, callback) => {
    const listeners = messageListeners.get(messageType);
    if (listeners) {
      const index = listeners.indexOf(callback);
      if (index > -1) {
        listeners.splice(index, 1);
