     1import { ref, reactive, onUnmounted } from 'vue';
     2import { useWebWorker } from './useWebWorker';
     3
     4/**
     5 * Centralized Web Worker management composable
     6 * Provides high-level worker lifecycle management with error recovery and monitoring
     7 */
     8export function useWorkerManager() {
     9  // Worker instances registry
    10  const workers = reactive(new Map());
    11
    12  // Global worker state
    13  const globalWorkerState = ref({
    14    totalWorkers: 0,
    15    activeWorkers: 0,
    16    failedWorkers: 0,
    17    healthyWorkers: 0,
    18  });
    19
    20  // Worker management options
    21  const defaultOptions = {
    22    autoRestart: true,
    23    maxRestartAttempts: 3,
    24    restartDelay: 1000, // 1 second
    25    healthCheckInterval: 30000, // 30 seconds
    26    enableMonitoring: true,
    27  };
    28
    29  // Create or get a worker instance
    30  const createWorker = (workerId, workerPath, options = {}) => {
    31    const config = { ...defaultOptions, ...options };
    32
    33    if (workers.has(workerId)) {
    34      console.warn(`Worker ${workerId} already exists`);
    35      return workers.get(workerId);
    36    }
    37
    38    const workerInstance = useWebWorker(workerPath);
    39    const workerState = reactive({
    40      id: workerId,
    41      path: workerPath,
    42      instance: workerInstance,
    43      config,
    44      restartAttempts: 0,
    45      lastRestart: null,
    46      createdAt: Date.now(),
    47      errors: [],
    48      stats: {
    49        messagesProcessed: 0,
    50        errorsEncountered: 0,
    51        averageResponseTime: 0,
    52        lastActivity: null,
    53      },
    54    });
    55
    56    // Initialize the worker
    57    const initialized = workerInstance.initializeWorker();
    58    if (!initialized) {
    59      console.error(`Failed to initialize worker ${workerId}`);
    60      return null;
    61    }
    62
    63    // Set up error monitoring
    64    setupWorkerMonitoring(workerState);
    65
    66    workers.set(workerId, workerState);
    67    updateGlobalState();
    68
    69    return workerState;
    70  };
    71
    72  // Set up monitoring for a worker
    73  const setupWorkerMonitoring = (workerState) => {
    74    const { instance, config } = workerState;
    75
    76    if (!config.enableMonitoring) return;
    77
    78    // Monitor worker errors
    79    const originalSendMessage = instance.sendMessage;
    80    instance.sendMessage = async (message, options = {}) => {
    81      const startTime = Date.now();
    82
    83      try {
    84        const result = await originalSendMessage(message, options);
    85
    86        // Update stats on success
    87        const responseTime = Date.now() - startTime;
    88        workerState.stats.messagesProcessed++;
    89        workerState.stats.lastActivity = Date.now();
    90
    91        // Update average response time
    92        const { averageResponseTime, messagesProcessed } = workerState.stats;
    93        workerState.stats.averageResponseTime =
    94          (averageResponseTime * (messagesProcessed - 1) + responseTime) / messagesProcessed;
    95
    96        return result;
    97      } catch (error) {
    98        // Handle worker errors
    99        workerState.stats.errorsEncountered++;
   100        workerState.errors.push({
   101          timestamp: Date.now(),
   102          message: error.message,
   103          type: 'processing-error',
   104        });
   105
   106        // Auto-restart on critical errors if enabled
   107        if (config.autoRestart && shouldRestartWorker(workerState, error)) {
   108          console.warn(`Auto-restarting worker ${workerState.id} due to error:`, error.message);
   109          await restartWorker(workerState.id);
   110        }
   111
   112        throw error;
   113      }
   114    };
   115  };
   116
   117  // Determine if worker should be restarted based on error
   118  const shouldRestartWorker = (workerState, error) => {
   119    const { restartAttempts, config } = workerState;
   120
   121    // Don't restart if max attempts reached
   122    if (restartAttempts >= config.maxRestartAttempts) {
   123      return false;
   124    }
   125
   126    // Restart on timeout, worker errors, or health failures
   127    const restartableErrors = ['timeout', 'Worker error', 'unhealthy'];
   128    return restartableErrors.some((errorType) =>
   129      error.message.toLowerCase().includes(errorType.toLowerCase())
   130    );
   131  };
   132
   133  // Restart a specific worker
   134  const restartWorker = async (workerId) => {
   135    const workerState = workers.get(workerId);
   136    if (!workerState) {
   137      console.error(`Worker ${workerId} not found`);
