    39    if (!workerJustInitialized && !workerInstance.isWorkerHealthy.value) {
    40      console.warn('Web Worker is unhealthy, attempting restart...');
    41
    42      // Use worker manager for restart if available
    43      let restarted = false;
    44      if (workerState) {
    45        restarted = await workerManager.restartWorker(WORKER_ID);
    46      } else {
    47        restarted = workerInstance.restartWorker();
    48      }
    49
    50      if (!restarted) {
    51        console.warn('Web Worker restart failed, falling back to main thread processing');
    52        return { healthy: false, fallback: true };
    53      }
    54    }
    55
    56    return { healthy: true, fallback: false };
    57  };
    58
    59  // Process files using web worker
    60  const processFilesWithWorker = async (files, onProgress = null) => {
    61    // Check if Web Workers are supported
    62    if (!workerInstance.isWorkerSupported) {
    63      console.warn(
    64        'Web Workers not supported in this browser, falling back to main thread processing'
    65      );
    66      return { success: false, fallback: true };
    67    }
    68
    69    // Check worker manager state if available
    70    if (workerState) {
    71      const stats = workerManager.getWorkerStatistics();
    72      console.debug('Worker statistics:', stats);
    73    }
    74
    75    // Initialize worker if needed
    76    const initResult = await initializeWorkerIfNeeded();
    77    if (!initResult.success) {
    78      return { success: false, fallback: true };
    79    }
    80
    81    // Check worker health
    82    const healthResult = await checkWorkerHealth(initResult.justInitialized);
    83    if (!healthResult.healthy) {
    84      return { success: false, fallback: true };
    85    }
    86
    87    try {
    88      // Create mapping structure that preserves original File objects
    89      const fileMapping = new Map();
