   131
   132        const workerResult = await workerInstance.sendMessage(
   133          {
   134            type: 'PROCESS_FILES',
   135            files: filesToProcess,
   136          },
   137          {
   138            timeout: estimatedTime,
   139          }
   140        );
   141
   142        // Map worker results back to original File objects
   143        const readyFiles = workerResult.readyFiles.map((fileRef) => ({
   144          ...fileRef,
   145          file: fileMapping.get(fileRef.id), // Restore original File object
   146          path: fileRef.path, // Preserve path from worker result
   147          status: 'ready',
   148        }));
   149
   150        const duplicateFiles = workerResult.duplicateFiles.map((fileRef) => ({
   151          ...fileRef,
   152          file: fileMapping.get(fileRef.id), // Restore original File object
   153          path: fileRef.path, // Preserve path from worker result
   154          status: 'uploadMetadataOnly',
   155        }));
   156
   157        return {
   158          success: true,
   159          result: { readyFiles, duplicateFiles },
   160        };
   161      } finally {
   162        // Clean up progress listener
   163        if (progressUnsubscribe) {
   164          progressUnsubscribe();
   165        }
   166      }
   167    } catch (error) {
   168      const appError = createApplicationError(error, {
   169        source: 'worker',
   170        fileCount: files.length,
   171        processingMode: 'worker',
   172      });
   173
   174      console.error('Web Worker processing failed:', appError);
   175
   176      // Determine if we should restart the worker or just fallback
   177      if (isRecoverableError(appError.type)) {
   178        console.warn('Worker appears to be stuck, restarting...');
   179
   180        // Use worker manager for restart if available
   181        if (workerState) {
   182          await workerManager.restartWorker(WORKER_ID);
   183        } else {
   184          workerInstance.restartWorker();
   185        }
   186      }
   187
   188      console.info('Falling back to main thread processing...');
   189      return { success: false, fallback: true, error: appError };
   190    }
   191  };
   192
   193  // Get processing status
   194  const getProcessingStatus = () => {
   195    const workerStatus = workerInstance.getWorkerStatus();
   196    const managerStats = workerState ? workerManager.getWorkerStatistics() : null;
   197
   198    return {
   199      ...workerStatus,
   200      fallbackAvailable: true,
   201      recommendedMode: workerStatus.supported && workerStatus.healthy ? 'worker' : 'fallback',
   202      workerManager: managerStats,
   203      managedWorker: workerState
   204        ? {
   205            id: WORKER_ID,
   206            restartAttempts: workerState.restartAttempts,
   207            lastRestart: workerState.lastRestart,
   208            errors: workerState.errors.length,
   209            stats: workerState.stats,
   210          }
   211        : null,
   212    };
   213  };
   214
   215  // Force worker restart
   216  const forceWorkerRestart = async () => {
   217    if (workerState) {
   218      return await workerManager.restartWorker(WORKER_ID);
   219    } else {
   220      return workerInstance.restartWorker();
   221    }
   222  };
   223
   224  // Terminate worker (cleanup)
   225  const terminateWorker = () => {
   226    if (import.meta.env.DEV) {
   227      console.debug(
   228        '[QueueWorkers] Terminating worker. State exists:',
   229        !!workerState,
   230        'Worker ID:',
   231        WORKER_ID
   232      );
   233      console.debug('[QueueWorkers] Worker instance exists:', !!workerInstance);
   234      console.debug('[QueueWorkers] Worker instance ready:', workerInstance?.isWorkerReady?.value);
   235    }
   236
   237    // Always terminate both the managed worker (if exists) and the direct worker instance
   238    let managerResult = true;
   239    let instanceResult = true;
   240
   241    if (workerState) {
   242      if (import.meta.env.DEV) {
   243        console.debug('[QueueWorkers] Terminating managed worker');
   244      }
   245      managerResult = workerManager.terminateWorker(WORKER_ID);
   246    }
   247
   248    // Also terminate the direct worker instance (fallback case)
   249    if (workerInstance && workerInstance.isWorkerReady?.value) {
   250      if (import.meta.env.DEV) {
   251        console.debug('[QueueWorkers] Terminating direct worker instance');
   252      }
   253      workerInstance.terminateWorker();
   254      instanceResult = true;
   255    }
   256
   257    if (import.meta.env.DEV) {
   258      console.debug(
   259        '[QueueWorkers] Termination complete. Manager result:',
   260        managerResult,
   261        'Instance result:',
   262        instanceResult
   263      );
   264    }
   265
   266    return managerResult && instanceResult;
   267  };
   268
   269  return {
   270    // Worker management
   271    workerInstance,
   272    workerManager,
   273    workerState,
   274    WORKER_ID,
   275
   276    // Worker operations
   277    initializeWorkerIfNeeded,
   278    checkWorkerHealth,
   279    processFilesWithWorker,
   280    getProcessingStatus,
   281    forceWorkerRestart,
   282    terminateWorker,
   283  };
   284}
