     3â†’import { useFileQueueCore } from '../../../composables/useFileQueueCore';
     4â†’
     5â†’export function useFileQueue() {
     6â†’  // Initialize core queue management
     7â†’  const queueCore = useFileQueueCore();
     8â†’
     9â†’  // Time monitoring for cloud file detection
    10â†’  let timeWarningInstance = null;
    11â†’
    12â†’  // UI-specific reactive data
