    50â†’      current: progressData.current || 0,
    51â†’      total: progressData.total || 0,
    52â†’      percentage: progressData.percentage || 0,
    53â†’      currentFile: progressData.currentFile || '',
    54â†’      isProcessing: true,
    55â†’    };
    56â†’  };
    57â†’
    58â†’  // Reset progress when processing completes
    59â†’  const resetProgress = () => {
    60â†’    processingProgress.value = {
    61â†’      current: 0,
    62â†’      total: 0,
    63â†’      percentage: 0,
    64â†’      currentFile: '',
    65â†’      isProcessing: false,
    66â†’    };
    67â†’  };
    68â†’
    69â†’  // Reset UI update progress
    70â†’  const resetUIProgress = () => {
    71â†’    isProcessingUIUpdate.value = false;
    72â†’    uiUpdateProgress.value = {
    73â†’      current: 0,
    74â†’      total: 0,
    75â†’      percentage: 0,
    76â†’      phase: 'loading',
    77â†’    };
    78â†’  };
    79â†’
    80â†’  // Set phase to complete (called when upload actually starts)
    81â†’  const setPhaseComplete = () => {
    82â†’    uiUpdateProgress.value.phase = 'complete';
    83â†’  };
    84â†’
    85â†’  // Upload status management
    86â†’  const resetUploadStatus = () => {
    87â†’    uploadStatus.value = {
    88â†’      successful: 0,
    89â†’      failed: 0,
    90â†’      skipped: 0,
    91â†’      isUploading: false,
    92â†’      isPaused: false,
    93â†’      pauseRequested: false,
    94â†’      currentFile: null,
    95â†’      currentAction: null,
    96â†’      currentUploadIndex: 0,
    97â†’    };
    98â†’  };
    99â†’
   100â†’  // Individual file status update function
   101â†’  const updateFileStatus = (fileReference, status) => {
   102â†’    // Support both file object and filename for backward compatibility
   103â†’    let file;
   104â†’    if (typeof fileReference === 'string') {
   105â†’      // Legacy filename-based lookup (less reliable but kept for compatibility)
   106â†’      file = queueCore.uploadQueue.value.find((f) => f.name === fileReference);
   107â†’    } else if (fileReference && typeof fileReference === 'object') {
   108â†’      // Direct file object reference (preferred approach)
   109â†’      file = fileReference;
   110â†’    }
   111â†’
   112â†’    if (file) {
   113â†’      file.status = status;
   114â†’    }
   115â†’  };
   116â†’
   117â†’  // Update all ready files to "ready" status after deduplication completes
   118â†’  const updateAllFilesToReady = () => {
   119â†’    queueCore.uploadQueue.value.forEach((file) => {
   120â†’      if (!file.isDuplicate && (!file.status || file.status === 'pending')) {
   121â†’        file.status = 'ready';
   122â†’      }
   123â†’    });
   124â†’  };
   125â†’
   126â†’  const updateUploadStatus = (type, fileName = null, action = null) => {
   127â†’    switch (type) {
   128â†’      case 'start':
   129â†’        uploadStatus.value.isUploading = true;
   130â†’        uploadStatus.value.isPaused = false;
   131â†’        uploadStatus.value.pauseRequested = false;
   132â†’        break;
   133â†’      case 'complete':
   134â†’        uploadStatus.value.isUploading = false;
   135â†’        uploadStatus.value.isPaused = false;
   136â†’        uploadStatus.value.pauseRequested = false;
   137â†’        uploadStatus.value.currentFile = null;
   138â†’        uploadStatus.value.currentAction = null;
   139â†’        uploadStatus.value.currentUploadIndex = 0;
   140â†’        break;
   141â†’      case 'pause':
   142â†’        uploadStatus.value.isPaused = true;
   143â†’        uploadStatus.value.isUploading = false;
   144â†’        uploadStatus.value.pauseRequested = false;
   145â†’        break;
   146â†’      case 'resume':
   147â†’        uploadStatus.value.isPaused = false;
   148â†’        uploadStatus.value.isUploading = true;
   149â†’        uploadStatus.value.pauseRequested = false;
   150â†’        break;
   151â†’      case 'requestPause':
   152â†’        uploadStatus.value.pauseRequested = true;
   153â†’        break;
   154â†’      case 'successful':
   155â†’        uploadStatus.value.successful++;
   156â†’        break;
   157â†’      case 'failed':
   158â†’        uploadStatus.value.failed++;
   159â†’        break;
   160â†’      case 'skipped':
   161â†’        uploadStatus.value.skipped++;
   162â†’        break;
   163â†’      case 'currentFile':
   164â†’        uploadStatus.value.currentFile = fileName;
   165â†’        uploadStatus.value.currentAction = action;
   166â†’        break;
   167â†’      case 'setUploadIndex':
   168â†’        uploadStatus.value.currentUploadIndex = fileName; // reusing fileName param for index
   169â†’        break;
   170â†’    }
   171â†’  };
   172â†’
   173â†’  // Instant Upload Queue initialization - show immediately with first 100 files
   174â†’  const initializeQueueInstantly = async (files) => {
   175â†’    const totalFiles = files.length;
   176â†’
   177â†’    // Show Upload Queue immediately
   178â†’    isProcessingUIUpdate.value = true;
   179â†’    uiUpdateProgress.value = {
   180â†’      current: 0,
   181â†’      total: totalFiles,
   182â†’      percentage: 0,
   183â†’      phase: 'loading',
   184â†’    };
   185â†’
   186â†’    // Force Vue reactivity update
   187â†’    await nextTick();
   188â†’
   189â†’    // Process first 100 files instantly for immediate display
   190â†’    const initialFiles = files.slice(0, 100).map((file) => ({
   191â†’      id: crypto.randomUUID(),
   192â†’      file: file,
   193â†’      metadata: {},
   194â†’      status: 'ready',
   195â†’      name: file.name,
   196â†’      size: file.size,
   197â†’      type: file.type,
   198â†’      lastModified: file.lastModified,
   199â†’      path: file.path,
   200â†’      isDuplicate: false,
   201â†’    }));
   202â†’
   203â†’    queueCore.uploadQueue.value = initialFiles;
   204â†’
   205â†’    if (totalFiles <= 100) {
   206â†’      uiUpdateProgress.value = {
   207â†’        current: totalFiles,
   208â†’        total: totalFiles,
   209â†’        percentage: 100,
   210â†’        phase: 'awaiting-upload',
   211â†’      };
   212â†’      isProcessingUIUpdate.value = false;
   213â†’    } else {
   214â†’      uiUpdateProgress.value = {
   215â†’        current: 100,
   216â†’        total: totalFiles,
   217â†’        percentage: Math.round((100 / totalFiles) * 100),
   218â†’        phase: 'loading',
   219â†’      };
   220â†’    }
   221â†’  };
   222â†’
   223â†’  // Simple 2-chunk UI updates for optimal user feedback
   224â†’  const updateFromWorkerResults = async (readyFiles, duplicateFiles) => {
   225â†’    const allFiles = [...readyFiles, ...duplicateFiles];
   226â†’    const totalFiles = allFiles.length;
   227â†’
   228â†’    // Start UI update process (if not already started by initializeQueueInstantly)
   229â†’    if (!isProcessingUIUpdate.value) {
   230â†’      isProcessingUIUpdate.value = true;
   231â†’      uiUpdateProgress.value = {
   232â†’        current: 0,
   233â†’        total: totalFiles,
   234â†’        percentage: 0,
   235â†’        phase: 'loading',
   236â†’      };
   237â†’    }
   238â†’
   239â†’    if (totalFiles <= 100) {
   240â†’      // For small file sets, just load everything at once
   241â†’      queueCore.uploadQueue.value = queueCore.processFileChunk(allFiles);
   242â†’
   243â†’      uiUpdateProgress.value = {
   244â†’        current: totalFiles,
   245â†’        total: totalFiles,
   246â†’        percentage: 100,
   247â†’        phase: 'awaiting-upload',
   248â†’      };
   249â†’
   250â†’      // Wait for Vue to complete DOM rendering for single chunk
   251â†’      await nextTick();
   252â†’      await new Promise((resolve) => setTimeout(resolve, 0));
   253â†’    } else {
   254â†’      // For large file sets, check if queue was already initialized instantly
   255â†’      if (queueCore.uploadQueue.value.length > 0) {
   256â†’        // Queue was already initialized with first 100 files
   257â†’        // Ensure minimum loading display time (at least 1.5 seconds for spinner visibility)
   258â†’        const minLoadingTime = 1500;
   259â†’        const elapsedTime = window.instantQueueStartTime
   260â†’          ? Date.now() - window.instantQueueStartTime
   261â†’          : 0;
   262â†’        const remainingTime = Math.max(0, minLoadingTime - elapsedTime);
   263â†’
   264â†’        if (remainingTime > 0) {
   265â†’          await new Promise((resolve) => setTimeout(resolve, remainingTime));
   266â†’        }
   267â†’
   268â†’        // Then replace with full processed results
   269â†’        queueCore.uploadQueue.value = queueCore.processFileChunk(allFiles);
   270â†’
   271â†’        uiUpdateProgress.value = {
   272â†’          current: totalFiles,
   273â†’          total: totalFiles,
   274â†’          percentage: 100,
   275â†’          phase: 'awaiting-upload',
   276â†’        };
   277â†’
   278â†’        // Clean up timestamp
   279â†’        window.instantQueueStartTime = null;
   280â†’      } else {
   281â†’        // Fallback to original 2-chunk strategy if queue wasn't pre-initialized
   282â†’        // CHUNK 1: Initial batch (first 100 files) - immediate user feedback
   283â†’        const chunk1Size = 100;
   284â†’        const chunk1Files = allFiles.slice(0, chunk1Size);
   285â†’        queueCore.uploadQueue.value = queueCore.processFileChunk(chunk1Files);
   286â†’
   287â†’        uiUpdateProgress.value = {
   288â†’          current: chunk1Size,
   289â†’          total: totalFiles,
   290â†’          percentage: Math.round((chunk1Size / totalFiles) * 100),
   291â†’          phase: 'loading',
   292â†’        };
   293â†’
   294â†’        // Brief delay to let user see the initial files and get visual feedback
   295â†’        await new Promise((resolve) => setTimeout(resolve, 200));
   296â†’
   297â†’        // CHUNK 2: Full render of ALL files
   298â†’        queueCore.uploadQueue.value = queueCore.processFileChunk(allFiles);
   299â†’
   300â†’        uiUpdateProgress.value = {
   301â†’          current: totalFiles,
   302â†’          total: totalFiles,
   303â†’          percentage: 100,
   304â†’          phase: 'awaiting-upload',
   305â†’        };
   306â†’      }
   307â†’
   308â†’      // Wait for Vue to complete DOM rendering
   309â†’      await nextTick();
   310â†’      await new Promise((resolve) => setTimeout(resolve, 0));
   311â†’    }
   312â†’
   313â†’    logProcessingTime('ALL_FILES_DISPLAYED');
   314â†’
   315â†’    // Clean up timing variables
   316â†’    if (window.endToEndStartTime) {
   317â†’      window.endToEndStartTime = null;
   318â†’    }
   319â†’    if (window.folderProcessingStartTime) {
   320â†’      window.folderProcessingStartTime = null;
   321â†’    }
   322â†’
   323â†’    // Complete the UI update process
   324â†’    isProcessingUIUpdate.value = false;
   325â†’
   326â†’    // Stop time monitoring when processing completes
   327â†’    if (timeWarningInstance) {
   328â†’      timeWarningInstance.stopMonitoring();
   329â†’    }
   330â†’
   331â†’    // Reset processing progress when complete
   332â†’    resetProgress();
   333â†’  };
   334â†’
