     3â†’export function useFolderAnalysis() {
     4â†’  // Single preprocessing function to parse all paths once and extract all needed information
     5â†’  const preprocessFileData = (files) => {
     6â†’    const directories = new Map(); // directory path -> depth
     7â†’    const fileDepths = [];
     8â†’    const rootFolders = new Set();
     9â†’    const preprocessedFiles = [];
    10â†’    let hasSubfolders = false;
    11â†’
    12â†’    files.forEach((fileData) => {
    13â†’      // Parse path once and extract all information
    14â†’      const pathParts = fileData.path.split('/').filter((part) => part !== '');
    15â†’      const fileDepth = pathParts.length - 1; // Subtract 1 because last part is filename
    16â†’      const isMainFolder = pathParts.length === 2; // Exactly 2 parts: folder/file (no subfolders)
    17â†’
    18â†’      // Track root folders and subfolder detection
    19â†’      rootFolders.add(pathParts[0]);
    20â†’      if (pathParts.length > 2) {
    21â†’        hasSubfolders = true;
    22â†’      }
    23â†’
    24â†’      // Collect file depth for file depth stats
    25â†’      fileDepths.push(fileDepth);
    26â†’
    27â†’      // Add unique directory paths (all levels) with their depths
    28â†’      const dirParts = pathParts.slice(0, -1); // Remove filename
    29â†’      for (let i = 1; i <= dirParts.length; i++) {
    30â†’        const dirPath = dirParts.slice(0, i).join('/');
    31â†’        const dirDepth = i; // Directory depth is its level in the hierarchy
    32â†’        directories.set(dirPath, dirDepth);
    33â†’      }
    34â†’
    35â†’      // Store preprocessed file data
    36â†’      preprocessedFiles.push({
    37â†’        ...fileData,
    38â†’        pathParts,
    39â†’        fileDepth,
    40â†’        isMainFolder,
    41â†’        dirParts,
    42â†’      });
    43â†’    });
    44â†’
    45â†’    // Calculate directory and file depth statistics
    46â†’    const maxFileDepth = fileDepths.length > 0 ? Math.max(...fileDepths) : 0;
    47â†’    const avgFileDepth =
    48â†’      fileDepths.length > 0 ? fileDepths.reduce((sum, d) => sum + d, 0) / fileDepths.length : 0;
    49â†’
    50â†’    const directoryDepths = Array.from(directories.values());
    51â†’    const avgDirectoryDepth =
    52â†’      directoryDepths.length > 0
    53â†’        ? directoryDepths.reduce((sum, d) => sum + d, 0) / directoryDepths.length
    54â†’        : 0;
    55â†’
    56â†’    return {
    57â†’      preprocessedFiles,
    58â†’      directoryStats: {
    59â†’        totalDirectoryCount: directories.size,
    60â†’        maxFileDepth: maxFileDepth,
    61â†’        avgFileDepth: Math.round(avgFileDepth * 10) / 10,
    62â†’        avgDirectoryDepth: Math.round(avgDirectoryDepth * 10) / 10,
    63â†’      },
    64â†’      folderStats: {
    65â†’        rootFolderCount: rootFolders.size,
    66â†’        hasSubfolders,
    67â†’      },
    68â†’    };
    69â†’  };
    70â†’
    71â†’  const calculateFileSizeMetrics = (files) => {
    72â†’    const fileSizes = files.map((f) => f.file.size);
    73â†’    const totalSizeMB = fileSizes.reduce((sum, size) => sum + size, 0) / (1024 * 1024);
    74â†’
    75â†’    // Group files by size to find identical sizes
    76â†’    const sizeGroups = new Map();
    77â†’    fileSizes.forEach((size) => {
    78â†’      sizeGroups.set(size, (sizeGroups.get(size) || 0) + 1);
    79â†’    });
    80â†’
    81â†’    const uniqueFiles = Array.from(sizeGroups.values()).filter((count) => count === 1).length;
    82â†’    const identicalSizeFiles = fileSizes.length - uniqueFiles;
    83â†’    const zeroByteFiles = fileSizes.filter((size) => size === 0).length;
    84â†’
    85â†’    // Get top 5 largest files
    86â†’    const sortedSizes = [...fileSizes].sort((a, b) => b - a);
    87â†’    const largestFileSizesMB = sortedSizes
    88â†’      .slice(0, 5)
    89â†’      .map((size) => Math.round((size / (1024 * 1024)) * 10) / 10);
    90â†’
    91â†’    return {
    92â†’      totalSizeMB: Math.round(totalSizeMB * 10) / 10,
    93â†’      uniqueFiles,
    94â†’      identicalSizeFiles,
    95â†’      zeroByteFiles,
    96â†’      largestFileSizesMB,
    97â†’    };
    98â†’  };
    99â†’
   100â†’  const calculateFilenameStats = (files) => {
   101â†’    const filenameLengths = files.map((f) => f.path.length);
   102â†’    const avgFilenameLength =
   103â†’      filenameLengths.length > 0
   104â†’        ? Math.round(
   105â†’            (filenameLengths.reduce((sum, len) => sum + len, 0) / filenameLengths.length) * 10
   106â†’          ) / 10
   107â†’        : 0;
   108â†’
   109â†’    return { avgFilenameLength };
   110â†’  };
   111â†’
   112â†’  // Lightweight subfolder detection - check only first few files
   113â†’  const hasSubfoldersQuick = (files, maxCheck = 10) => {
   114â†’    const checkCount = Math.min(files.length, maxCheck);
   115â†’    for (let i = 0; i < checkCount; i++) {
   116â†’      const pathParts = files[i].path.split('/').filter((part) => part !== '');
   117â†’      if (pathParts.length > 2) {
   118â†’        return true;
   119â†’      }
   120â†’    }
