   122â†’  };
   123â†’
   124â†’  // Core analysis function using fileAnalysis utility
   125â†’  const performFileAnalysis = (files, directoryStats) => {
   126â†’    if (!files || files.length === 0) {
   127â†’      return analyzeFiles([], 0, 0, 0);
   128â†’    }
   129â†’    return analyzeFiles(
   130â†’      files,
