   133â†’      directoryStats.avgFileDepth
   134â†’    );
   135â†’  };
   136â†’
   137â†’  // Diagnostic function to count all entries in a directory (like File Explorer would)
   138â†’  const getDirectoryEntryCount = async (dirEntry) => {
   139â†’    return new Promise((resolve) => {
   140â†’      let totalFileCount = 0;
   141â†’      const reader = dirEntry.createReader();
   142â†’
   143â†’      const countAllEntries = () => {
   144â†’        reader.readEntries(
   145â†’          async (entries) => {
   146â†’            if (entries.length === 0) {
   147â†’              resolve(totalFileCount);
   148â†’              return;
   149â†’            }
   150â†’
   151â†’            for (const entry of entries) {
   152â†’              if (entry.isFile) {
   153â†’                totalFileCount++;
   154â†’              } else if (entry.isDirectory) {
   155â†’                // Recursively count files in subdirectories
   156â†’                try {
   157â†’                  const subCount = await getDirectoryEntryCount(entry);
   158â†’                  totalFileCount += subCount;
   159â†’                } catch (error) {
   160â†’                  // Skip directories we can't access
   161â†’                  console.warn(`Cannot access subdirectory ${entry.fullPath} for counting:`, error);
   162â†’                }
   163â†’              }
   164â†’            }
   165â†’
   166â†’            countAllEntries(); // Continue reading more entries
   167â†’          },
   168â†’          (error) => {
   169â†’            console.warn('Error during directory entry counting:', error);
   170â†’            resolve(totalFileCount);
   171â†’          }
   172â†’        );
   173â†’      };
   174â†’
   175â†’      countAllEntries();
   176â†’    });
   177â†’  };
   178â†’
   179â†’  const readDirectoryRecursive = (dirEntry, abortSignal = null) => {
   180â†’    return new Promise((resolve, reject) => {
   181â†’      const files = [];
   182â†’
   183â†’      // Early abort check
   184â†’      if (abortSignal?.aborted) {
   185â†’        resolve([]);
   186â†’        return;
   187â†’      }
   188â†’
   189â†’      const reader = dirEntry.createReader();
   190â†’
   191â†’      // Handle abort during operation
   192â†’      const handleAbort = () => {
   193â†’        console.log(`Directory read aborted: ${dirEntry.fullPath}`);
   194â†’        if (abortSignal?.onSkipFolder) {
   195â†’          abortSignal.onSkipFolder(dirEntry.fullPath);
   196â†’        }
   197â†’        resolve([]); // Return empty array on abort
   198â†’      };
   199â†’
   200â†’      if (abortSignal) {
   201â†’        abortSignal.addEventListener('abort', handleAbort);
   202â†’      }
   203â†’
   204â†’      const readEntries = async () => {
   205â†’        try {
   206â†’          // Check abort status before each readEntries call
   207â†’          if (abortSignal?.aborted) {
   208â†’            resolve([]);
   209â†’            return;
   210â†’          }
   211â†’
   212â†’          reader.readEntries(
   213â†’            async (entries) => {
   214â†’              try {
   215â†’                // Check abort after getting entries
   216â†’                if (abortSignal?.aborted) {
   217â†’                  resolve([]);
   218â†’                  return;
   219â†’                }
   220â†’
   221â†’                if (entries.length === 0) {
   222â†’                  // Report progress on successful completion
   223â†’                  if (abortSignal?.onProgress) {
   224â†’                    abortSignal.onProgress(files.length);
   225â†’                  }
   226â†’
   227â†’                  // Reset global timeout on successful directory read
   228â†’                  if (abortSignal?.resetGlobalTimeout) {
   229â†’                    abortSignal.resetGlobalTimeout();
   230â†’                  }
   231â†’
   232â†’                  resolve(files);
   233â†’                  return;
   234â†’                }
   235â†’
   236â†’                for (const entry of entries) {
   237â†’                  // Check abort before processing each entry
   238â†’                  if (abortSignal?.aborted) {
   239â†’                    resolve(files); // Return whatever we have so far
   240â†’                    return;
   241â†’                  }
   242â†’
   243â†’                  if (entry.isFile) {
   244â†’                    try {
   245â†’                      const file = await new Promise((resolve, reject) => {
   246â†’                        const timeoutId = setTimeout(() => {
   247â†’                          reject(new Error('File read timeout'));
   248â†’                        }, 5000); // 5 second timeout for individual files
   249â†’
   250â†’                        entry.file(
   251â†’                          (file) => {
