   253â†’                            resolve(file);
   254â†’                          },
   255â†’                          (error) => {
   256â†’                            clearTimeout(timeoutId);
   257â†’                            reject(error);
   258â†’                          }
   259â†’                        );
   260â†’                      });
   261â†’
   262â†’                      files.push({ file, path: entry.fullPath });
   263â†’
   264â†’                      // Report incremental progress
   265â†’                      if (abortSignal?.onProgress) {
   266â†’                        abortSignal.onProgress(files.length);
   267â†’                      }
   268â†’                    } catch (error) {
   269â†’                      // Cloud files show NotFoundError when not locally available
   270â†’                      // This is expected behavior - count and continue silently
   271â†’                      if (error.name === 'NotFoundError') {
   272â†’                        // Track cloud-only files for user feedback
   273â†’                        if (abortSignal?.onCloudFile) {
   274â†’                          abortSignal.onCloudFile(entry.fullPath, error);
   275â†’                        }
   276â†’                      } else {
   277â†’                        // Log unexpected file errors for debugging
   278â†’                        console.warn(`Unexpected file read error for ${entry.fullPath}:`, error);
   279â†’                      }
   280â†’                      // Continue processing other files
   281â†’                    }
   282â†’                  } else if (entry.isDirectory) {
