    showFolderOptions.value = false;
    pendingFolderFiles.value = [];

    // Clear timeout and reset state
    cleanup();
  };

  return {
    // Reactive data
    showFolderOptions,
    includeSubfolders,
    pendingFolderFiles,
    subfolderCount,

    // Analysis state
    isAnalyzing,
    mainFolderAnalysis,
    allFilesAnalysis,
    totalDirectoryEntryCount,

    // Timeout state (from timeout module)
    analysisTimedOut,
    timeoutError,
    currentProgressMessage,
    skippedFolders,

