// Vuetify
import 'vuetify/styles'
import { createVuetify } from 'vuetify'
import * as components from 'vuetify/components'
import * as directives from 'vuetify/directives'

// Material Design Icons
import '@mdi/font/css/materialdesignicons.css'

const vuetify = createVuetify({
  components,
  directives,
  theme: {
    defaultTheme: 'light',
    themes: {
      light: {
        colors: {
          primary: '#3b82f6', // brand-blue
          'primary-darken-1': '#1d4ed8', // brand-blue-dark
          secondary: '#64748b', // slate-500
          accent: '#f1f5f9', // slate-100
          error: '#ef4444', // red-500
          warning: '#f59e0b', // amber-500
          info: '#3b82f6', // blue-500
          success: '#10b981', // emerald-500
          surface: '#ffffff',
          'surface-variant': '#f8fafc', // slate-50
          'on-surface': '#1e293b', // slate-800
          'on-surface-variant': '#475569', // slate-600
        },
      },
      dark: {
        colors: {
          primary: '#3b82f6', // brand-blue
          'primary-darken-1': '#1d4ed8', // brand-blue-dark
          secondary: '#64748b', // slate-500
          accent: '#1e293b', // slate-800
          error: '#ef4444', // red-500
          warning: '#f59e0b', // amber-500
          info: '#3b82f6', // blue-500
          success: '#10b981', // emerald-500
          surface: '#1e293b', // slate-800
          'surface-variant': '#334155', // slate-700
          'on-surface': '#f1f5f9', // slate-100
          'on-surface-variant': '#cbd5e1', // slate-300
        },
      },
    },
  },
})

export default vuetify

import { createRouter, createWebHashHistory } from 'vue-router'
import { createAuthGuard } from './guards/auth' // Import the auth guard

const router = createRouter({
  history: createWebHashHistory(import.meta.env.BASE_URL),
  routes: [
    {
      path: '/',
      name: 'home',
      component: () => import('../views/Home.vue'),
      meta: { requiresAuth: true },
    },
    {
      path: '/home',
      name: 'home-explicit',
      component: () => import('../views/Home.vue'),
      meta: { requiresAuth: true },
    },
    {
      path: '/about',
      name: 'about',
      component: () => import('../views/About.vue'),
      meta: { requiresAuth: true },
    },
    {
      path: '/profile',
      name: 'profile',
      component: () => import('../views/Profile.vue'),
      meta: { requiresAuth: true },
    },
    {
      path: '/settings',
      name: 'settings',
      component: () => import('../views/Settings.vue'),
      meta: { requiresAuth: true },
    },
    {
      path: '/login',
      name: 'login',
      component: () => import('../components/features/auth/LoginForm.vue'),
      meta: { requiresAuth: false },
    },
    {
      path: '/under-construction',
      name: 'under-construction',
      component: () => import('../views/defaults/UnderConstruction.vue'),
      meta: { requiresAuth: true },
    },
    {
      path: '/404',
      name: 'page-not-found',
      component: () => import('../views/defaults/PageNotFound.vue'),
      meta: { requiresAuth: true },
    },
    {
      path: '/:pathMatch(.*)*',
      name: 'catch-all',
      redirect: '/404',
    },
  ],
})

// Apply the global beforeEach guard
router.beforeEach(createAuthGuard())

export default router

