} = useLazyFileList(computed(() => groupedFiles.value))

// Preload initial items for better UX
onMounted(() => {
  preloadInitialItems(10)
})

// Reset loaded items when files change
watch(() => props.files, () => {
  resetLoadedItems()
  // Re-preload initial items after files change
  preloadInitialItems(10)
}, { deep: true })

// Computed properties
const uploadableFiles = computed(() => {
  return props.files.filter(file => !file.isDuplicate)
})

const skippableFiles = computed(() => {
  return props.files.filter(file => file.isDuplicate)
})

// Group files for better duplicate visualization
const groupedFiles = computed(() => {
  const groups = new Map()

  // Group files by hash (for duplicates) or by unique ID (for singles)
  props.files.forEach(file => {
    const groupKey = file.hash || `unique_${file.name}_${file.size}_${file.lastModified}`

    if (!groups.has(groupKey)) {
      groups.set(groupKey, {
        files: [],
        isDuplicateGroup: false,
        groupName: file.name
      })
    }

    groups.get(groupKey).files.push(file)
  })

  // Mark groups with multiple files as duplicate groups
  for (const group of groups.values()) {
    if (group.files.length > 1) {
      group.isDuplicateGroup = true
      // Sort within group: kept files first, then duplicates
      group.files.sort((a, b) => {
        if (a.isDuplicate !== b.isDuplicate) {
          return a.isDuplicate ? 1 : -1 // Non-duplicates first
        }
        return a.originalIndex - b.originalIndex
      })
    }
  }

  return Array.from(groups.values())
})

// Watch for file changes and populate existing hashes
watch(() => props.files, populateExistingHashes, { deep: true })

const totalSize = computed(() => {
  return uploadableFiles.value.reduce((total, file) => total + file.size, 0)
})

const hasErrors = computed(() => {
  return props.files.some(file => file.status === 'error')
})


const pendingCount = computed(() => {
  return props.files.filter(file => 
    file.status === 'ready' || 
    file.status === 'pending' || 
    (!file.status && !file.isQueueDuplicate && !file.isPreviousUpload)
  ).length
})

const processingCount = computed(() => {
  return props.files.filter(file => file.status === 'processing').length
})

const queueDuplicatesCount = computed(() => {
  return props.files.filter(file => file.isDuplicate).length
})

const previouslyUploadedCount = computed(() => {
  return props.files.filter(file => file.isPreviousUpload).length
})

const successfulCount = computed(() => {
  return props.files.filter(file => file.status === 'completed').length
})

const failedCount = computed(() => {
  return props.files.filter(file => file.status === 'error').length
})

// Methods
const formatFileSize = (bytes) => {
  if (bytes === 0) return '0 B'

  const k = 1024
  const sizes = ['B', 'KB', 'MB', 'GB', 'TB']
  const i = Math.floor(Math.log(bytes) / Math.log(k))

  return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`
}

// 2-chunk loading message generator
const getLoadingMessage = () => {
  if (!props.isProcessingUIUpdate) return 'Loading files into queue...'

  const { total, percentage } = props.uiUpdateProgress

  if (total <= 100) {
    return 'Loading files into queue...'
  }

  if (percentage <= 15) {
    return 'Showing initial files...'
  } else if (percentage < 100) {
    return 'Loading remaining files...'
  } else {
    return 'Complete!'
  }
}

// Phase message for 2-chunk strategy
const getPhaseMessage = () => {
  if (!props.isProcessingUIUpdate) return 'Loading...'

  const { total, percentage } = props.uiUpdateProgress

  if (total <= 100) {
    return 'Loading...'
  }

  if (percentage <= 15) {
    return 'Step 1/2'
  } else if (percentage < 100) {
    return 'Step 2/2'
  } else {
    return 'Complete!'
  }
}

</script>

<style scoped>
.gap-2 {
  gap: 8px;
}

.cursor-help {
  cursor: help;
}

/* Custom fade transition for tooltip popup */
:deep(.fade-transition-enter-active),
:deep(.fade-transition-leave-active) {
  transition: opacity 0.5s ease-in-out !important;
}

:deep(.fade-transition-enter-from),
:deep(.fade-transition-leave-to) {
  opacity: 0 !important;
}

:deep(.fade-transition-enter-to),
:deep(.fade-transition-leave-from) {
  opacity: 1 !important;
}
</style>

import { ref, computed } from 'vue'

/**
 * Composable for managing lazy loading state of file list items
 * Tracks which items have been loaded based on group and file index
 * Optimized for large file lists with efficient Set-based storage
 */
export function useLazyFileList(groupedFiles) {
  // Track loaded items using Set for O(1) lookup performance
  const loadedItems = ref(new Set())

  // Track loading progress for optional progress indicators
  const totalItemsCount = computed(() => {
    if (!groupedFiles?.value) return 0
    return groupedFiles.value.reduce((total, group) => total + group.files.length, 0)
  })

  const loadedItemsCount = computed(() => {
    return loadedItems.value.size
  })

  // Generate unique key for group/file combination
  const getItemKey = (groupIndex, fileIndex) => {
    return `${groupIndex}-${fileIndex}`
  }

  // Load an item (called when placeholder intersects viewport)
  const loadItem = (groupIndex, fileIndex) => {
    const key = getItemKey(groupIndex, fileIndex)
    loadedItems.value.add(key)
  }

  // Check if an item is loaded
  const isItemLoaded = (groupIndex, fileIndex) => {
    const key = getItemKey(groupIndex, fileIndex)
    return loadedItems.value.has(key)
  }

  // Reset all loaded items (useful when file list changes)
  const resetLoadedItems = () => {
    loadedItems.value.clear()
  }

  // Preload visible items (optional optimization for initial viewport)
  const preloadInitialItems = (count = 10) => {
    if (!groupedFiles?.value) return

    let loaded = 0
    for (let groupIndex = 0; groupIndex < groupedFiles.value.length && loaded < count; groupIndex++) {
      const group = groupedFiles.value[groupIndex]
      for (let fileIndex = 0; fileIndex < group.files.length && loaded < count; fileIndex++) {
        loadItem(groupIndex, fileIndex)
        loaded++
      }
    }
  }

  // Check if lazy loading is in progress
  const isLazyLoading = computed(() => {
    return totalItemsCount.value > 0 && loadedItemsCount.value < totalItemsCount.value
  })

  return {
    // Main API
    loadItem,
    isItemLoaded,
    resetLoadedItems,
    preloadInitialItems,

    // Progress tracking
    totalItemsCount,
    loadedItemsCount,
    isLazyLoading,

    // State access (for debugging)
    loadedItems: loadedItems.value
  }
}

