  // Mixed paths
  { name: 'mixed.txt', path: 'Documents\\Folder1/Subfolder\\mixed.txt', expected: '/Documents/Folder1/Subfolder' },

  // Edge cases
  { name: 'root.txt', path: 'root.txt', expected: '\\' },
  { name: 'empty.txt', path: '', expected: '\\' },
  { name: 'same.txt', path: 'same.txt', expected: '\\' }
]

console.log('Testing getRelativePath function fix...\n')

let passed = 0
let failed = 0

testCases.forEach((testCase, index) => {
  const file = { name: testCase.name, path: testCase.path }
