const continueUpload = async () => {
  try {
    console.log('Continuing upload process...');

    // Process duplicates first (skip them with proper logging)
    const duplicateFiles = uploadQueue.value.filter(file => file.isDuplicate);
    const filesToUpload = uploadQueue.value.filter(file => !file.isDuplicate);

    // Process duplicate files (skip them with logging and metadata)
    for (const duplicateFile of duplicateFiles) {
      console.log(`Skipping duplicate file: ${duplicateFile.name}`);
      updateFileStatus(duplicateFile.name, 'previouslyUploaded');
      updateUploadStatus('previouslyUploaded');

      // Log skip event for duplicate
      try {
        await logFileUploadEvent({
          fileName: duplicateFile.name,
          fileHash: duplicateFile.hash,
          fileSize: duplicateFile.size,
          lastModified: duplicateFile.lastModified,
          status: 'skipped',
          reason: 'queue_duplicate'
        });
      } catch (logError) {
        console.error(`Failed to log skip event for duplicate ${duplicateFile.name}:`, logError);
        // Don't fail the upload process for logging errors
      }

      // Create metadata record for skipped duplicate
      try {
        await createMetadataRecord({
          originalName: duplicateFile.name,
          lastModified: duplicateFile.lastModified,
          fileHash: duplicateFile.hash,
          sessionId: getCurrentSessionId()
        });
      } catch (metadataError) {
        console.error(`Failed to create metadata record for duplicate ${duplicateFile.name}:`, metadataError);
        // Don't fail the upload process for metadata errors
      }
    }

    if (filesToUpload.length === 0) {
      showNotification('No files to upload (all are duplicates)', 'info');
      updateUploadStatus('complete');
      return;
    }
    // Start from current index or beginning
    const startIndex = uploadStatus.value.currentUploadIndex || 0;
    console.log(`Continuing from file ${startIndex + 1} of ${filesToUpload.length}`);

