    console.error(`Failed to log skip event for duplicate ${duplicateFile.name}:`, logError);
    // Don't fail the upload process for logging errors
  }

  // Create metadata record for skipped duplicate
  try {
    await createMetadataRecord({
      originalName: duplicateFile.name,
      lastModified: duplicateFile.lastModified,
      fileHash: duplicateFile.hash,
      sessionId: getCurrentSessionId()
    });
  } catch (metadataError) {
    console.error(`Failed to create metadata record for duplicate ${duplicateFile.name}:`, metadataError);
    // Don't fail the upload process for metadata errors
  }
}

