      uploadedAt: serverTimestamp()
    }, teamId)

    return { status: 'uploaded', hash, storagePath }
  } catch (error) {
    // If storage upload failed, no metadata was written - system stays consistent
    throw new Error(`Upload failed: ${error.message}`)
  }
}
