    if (!this.fileMatrix[x]) this.fileMatrix[x] = []
    if (!this.fileMatrix[x][y]) this.fileMatrix[x][y] = []
    if (!this.fileMatrix[x][y][z]) this.fileMatrix[x][y][z] = []

