uploadStore.addFiles([
  { hash: 'abc123', name: 'file1.pdf' },
  { hash: 'abc123', name: 'file1.pdf' }, // Automatically overwrites first
  { hash: 'def456', name: 'file2.pdf' }
])

// Access specific file
