export function performHardwareCalibrationTest() {
  console.log('ðŸ”§ HARDWARE_CALIBRATION_CPU_START:', performance.now())

  const startTime = performance.now()

  // Fixed complexity operation: 10,000 mathematical calculations
  let sum = 0
  let product = 1

  for (let i = 1; i <= 10000; i++) {
    // Mix of operations to simulate CPU-bound work
    sum += Math.sqrt(i)
    product *= (i % 7 === 0) ? 1.001 : 0.999

    // Add some trigonometric operations every 100 iterations
    if (i % 100 === 0) {
      sum += Math.sin(i / 1000) * Math.cos(i / 2000)
    }
  }

  const endTime = performance.now()
  const calibrationTime = endTime - startTime

  console.log('ðŸ”§ HARDWARE_CALIBRATION_CPU_COMPLETE:', endTime, 'Time:', calibrationTime)

  // Calculate hardware factor using a baseline (e.g., 10ms on reference hardware)
  const REFERENCE_CALIBRATION_TIME = 10.0 // Baseline time in milliseconds
  const hardwareFactor = calibrationTime / REFERENCE_CALIBRATION_TIME

  return {
    calibrationTimeMs: calibrationTime,
    hardwareFactor: hardwareFactor,
    baselineMs: REFERENCE_CALIBRATION_TIME,
    // Include the results to prevent optimization
    calculationResult: { sum, product }
  }
}

