/**
 * Hardware Calibration Utility
 * Implements the H factor calculation methodology using fixed-time operations
 * Formula structure: ((a + A) + (b + B) + (c + C) + d) Ã— H
 */

// Reference baseline times (measured on baseline hardware)
const REFERENCE_BASELINES = {
  CPU_CALIBRATION_MS: 10.0,        // 10ms for CPU test on reference hardware
  WORKER_INIT_MS: 50.0,           // 50ms for worker initialization on reference hardware
  BROWSER_API_MS: 5.0,            // 5ms for browser API test on reference hardware
  FOLDER_ANALYSIS_BASE_MS: 200.0   // 200ms baseline for folder analysis complexity
}

// Weights for combining different calibration methods
const CALIBRATION_WEIGHTS = {
  CPU_WEIGHT: 0.4,        // CPU performance is most important
  WORKER_WEIGHT: 0.3,     // Worker initialization reflects system overhead
  BROWSER_WEIGHT: 0.2,    // Browser API performance
  FOLDER_WEIGHT: 0.1      // Folder analysis as secondary indicator
}

/**
 * Calculate hardware factor H from multiple calibration measurements
 * @param {Object} measurements - Timing measurements from various tests
 * @returns {Object} Hardware calibration results including H factor
 */
export function calculateHardwareFactor(measurements) {
  const {
    cpuCalibrationMs,
    workerInitMs,
    browserAPIMs,
    folderAnalysisMs
  } = measurements

  console.log('ðŸ”§ CALCULATING_HARDWARE_FACTOR:', measurements)

  const factors = []
  const factorDetails = {}

  // CPU calibration factor (most reliable)
  if (cpuCalibrationMs && cpuCalibrationMs > 0) {
    const cpuFactor = cpuCalibrationMs / REFERENCE_BASELINES.CPU_CALIBRATION_MS
    factors.push({
      factor: cpuFactor,
      weight: CALIBRATION_WEIGHTS.CPU_WEIGHT,
      source: 'CPU'
    })
    factorDetails.cpuFactor = cpuFactor
    console.log(`ðŸ”§ CPU_FACTOR: ${cpuFactor.toFixed(3)} (${cpuCalibrationMs}ms / ${REFERENCE_BASELINES.CPU_CALIBRATION_MS}ms)`)
  }

  // Worker initialization factor
  if (workerInitMs && workerInitMs > 0) {
    const workerFactor = workerInitMs / REFERENCE_BASELINES.WORKER_INIT_MS
    factors.push({
      factor: workerFactor,
      weight: CALIBRATION_WEIGHTS.WORKER_WEIGHT,
      source: 'Worker'
    })
    factorDetails.workerFactor = workerFactor
    console.log(`ðŸ”§ WORKER_FACTOR: ${workerFactor.toFixed(3)} (${workerInitMs}ms / ${REFERENCE_BASELINES.WORKER_INIT_MS}ms)`)
  }

  // Browser API factor
  if (browserAPIMs && browserAPIMs > 0) {
    const browserFactor = browserAPIMs / REFERENCE_BASELINES.BROWSER_API_MS
    factors.push({
      factor: browserFactor,
      weight: CALIBRATION_WEIGHTS.BROWSER_WEIGHT,
      source: 'Browser'
    })
    factorDetails.browserFactor = browserFactor
    console.log(`ðŸ”§ BROWSER_FACTOR: ${browserFactor.toFixed(3)} (${browserAPIMs}ms / ${REFERENCE_BASELINES.BROWSER_API_MS}ms)`)
  }

  // Folder analysis complexity factor (secondary)
  if (folderAnalysisMs && folderAnalysisMs > 0) {
    const folderFactor = folderAnalysisMs / REFERENCE_BASELINES.FOLDER_ANALYSIS_BASE_MS
    factors.push({
      factor: folderFactor,
      weight: CALIBRATION_WEIGHTS.FOLDER_WEIGHT,
      source: 'FolderAnalysis'
    })
    factorDetails.folderFactor = folderFactor
    console.log(`ðŸ”§ FOLDER_FACTOR: ${folderFactor.toFixed(3)} (${folderAnalysisMs}ms / ${REFERENCE_BASELINES.FOLDER_ANALYSIS_BASE_MS}ms)`)
  }

  // Calculate weighted hardware factor
  if (factors.length === 0) {
    console.warn('ðŸ”§ No calibration measurements available, using default H = 1.0')
    return {
      hardwareFactor: 1.0,
      confidence: 0.0,
      method: 'default',
      details: { warning: 'No measurements available' }
    }
  }

  // Weighted average of all available factors
  const totalWeight = factors.reduce((sum, f) => sum + f.weight, 0)
  const weightedSum = factors.reduce((sum, f) => sum + (f.factor * f.weight), 0)
  const hardwareFactor = weightedSum / totalWeight

  // Calculate confidence based on number and consistency of measurements
  const variance = factors.reduce((sum, f) => {
    const diff = f.factor - hardwareFactor
    return sum + (diff * diff * f.weight)
  }, 0) / totalWeight

  const standardDeviation = Math.sqrt(variance)
  const confidence = Math.max(0, Math.min(1, 1 - (standardDeviation / hardwareFactor)))

  // Clamp hardware factor to reasonable bounds (0.1x to 10x)
  const clampedFactor = Math.max(0.1, Math.min(10.0, hardwareFactor))

  console.log(`ðŸ”§ HARDWARE_FACTOR_RESULT: ${clampedFactor.toFixed(3)} (confidence: ${(confidence * 100).toFixed(1)}%)`)

  return {
    hardwareFactor: clampedFactor,
    rawFactor: hardwareFactor,
    confidence: confidence,
    standardDeviation: standardDeviation,
    method: 'weighted_average',
    measurementsUsed: factors.length,
    details: factorDetails,
    factors: factors.map(f => ({
      source: f.source,
      factor: f.factor,
      weight: f.weight
    }))
  }
}

/**
 * Apply hardware factor to prediction formula
 * Implements: ((a + A) + (b + B) + (c + C) + d) Ã— H
 * @param {Object} basePrediction - Base timing prediction without hardware scaling
 * @param {number} hardwareFactor - Hardware calibration factor H
 * @returns {Object} Hardware-scaled prediction
 */
export function applyHardwareFactor(basePrediction, hardwareFactor) {
  const {
    phase1BaseMs,
    phase1VariableMs,
    phase2BaseMs,
    phase2VariableMs,
    phase3BaseMs,
    phase3VariableMs,
    overallBaseMs = 0
  } = basePrediction

  // Calculate components: (a + A) + (b + B) + (c + C) + d
  const phase1Total = (phase1BaseMs || 0) + (phase1VariableMs || 0)  // (a + A)
  const phase2Total = (phase2BaseMs || 0) + (phase2VariableMs || 0)  // (b + B)  
  const phase3Total = (phase3BaseMs || 0) + (phase3VariableMs || 0)  // (c + C)
  const overallBase = overallBaseMs                                  // d

  const totalBase = phase1Total + phase2Total + phase3Total + overallBase

  // Apply hardware factor: ((a + A) + (b + B) + (c + C) + d) Ã— H
  const hardwareScaledTotal = totalBase * hardwareFactor

  // Scale individual phases proportionally
  const scaledPhase1 = phase1Total * hardwareFactor
  const scaledPhase2 = phase2Total * hardwareFactor
  const scaledPhase3 = phase3Total * hardwareFactor

  console.log(`ðŸ”§ HARDWARE_SCALING: Base=${totalBase.toFixed(1)}ms Ã— H=${hardwareFactor.toFixed(3)} = ${hardwareScaledTotal.toFixed(1)}ms`)

  return {
    phase1Time: Math.max(10, Math.round(scaledPhase1)),
    phase2Time: Math.max(5, Math.round(scaledPhase2)),
    phase3Time: Math.max(10, Math.round(scaledPhase3)),
    totalEstimatedTime: Math.round(hardwareScaledTotal),
    hardwareFactor: hardwareFactor,
    baseTotal: totalBase,
    scalingApplied: true
  }
}

/**
