    43→              :disabled="!canCreateCategory"
    44→              @click="createCategory"
    45→            >
    46→              <v-icon start>mdi-plus</v-icon>
    47→              Create Category
    48→            </v-btn>
    49→          </v-col>
    50→        </v-row>
    51→      </v-card-text>
    52→    </v-card>
    53→
    54→    <!-- Existing Categories -->
    55→    <v-card variant="outlined">
    56→      <v-card-title>
    57→        <v-icon class="mr-2">mdi-folder-multiple</v-icon>
    58→        Existing Categories ({{ categories.length }})
    59→      </v-card-title>
    60→      <v-card-text>
    61→        <!-- Loading State -->
    62→        <div v-if="loading" class="text-center py-6">
    63→          <v-progress-circular indeterminate />
    64→          <p class="mt-2">Loading categories...</p>
    65→        </div>
    66→
    67→        <!-- Empty State -->
    68→        <div v-else-if="categories.length === 0" class="text-center py-6">
    69→          <v-icon size="64" color="grey">mdi-folder-outline</v-icon>
    70→          <p class="text-h6 mt-2">No categories yet</p>
