    82→              <v-expansion-panel-title>
    83→                <div class="d-flex align-center">
    84→                  <v-icon :color="category.color" class="mr-3">mdi-folder</v-icon>
    85→                  <div>
    86→                    <div class="font-weight-medium">{{ category.name }}</div>
    87→                    <div class="text-caption text-medium-emphasis">
    88→                      {{ (category.tags || []).length }} tags
    89→                    </div>
    90→                  </div>
    91→                </div>
    92→                <template #actions="{ expanded }">
    93→                  <v-btn
    94→                    icon
    95→                    variant="text"
    96→                    size="small"
    97→                    :color="expanded ? 'primary' : 'default'"
    98→                  >
    99→                    <v-icon>{{ expanded ? 'mdi-chevron-up' : 'mdi-chevron-down' }}</v-icon>
   100→                  </v-btn>
   101→                </template>
   102→              </v-expansion-panel-title>
   103→
   104→              <v-expansion-panel-text>
